import React, { useRef, useState } from "react";
import { ethers } from "ethers";
import { contractABI, contractAddress } from "../utils/constants";
import "./CampaignCard.css";
import { Redirect } from "react-router-dom";
const CampaignCardItem = (props) => {
  const { ethereum } = window;
  const [/*funded*/, setFunded] = useState(false);
  const [currentAccount, setCurrentAccount] = useState("");
  const [formData, setformData] = useState("");
  const createEthereumContract = () => {
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    const transactionsContract = new ethers.Contract(
      contractAddress,
      contractABI,
      signer
    );
    return transactionsContract;
  };

  const handleChange = (e) => {
    setformData(e.target.value);
  };

  async function connectWallet() {
    if (typeof ethereum !== "undefined") {
      const accounts = await ethereum.request({
        method: "eth_requestAccounts",
      });
      setCurrentAccount(accounts[0]);
    }
  }
  const sendETH = async (e) => {
    e.preventDefault();
    try {
      if (ethereum) {
        const addressTo = props.wallet;
        const transactionsContract = createEthereumContract();
        const parsedAmount = ethers.utils.parseEther(formData);
        await ethereum.request({
          method: "eth_sendTransaction",
          params: [
            {
              from: currentAccount,
              to: addressTo,
              gas: "0x5208",
              value: parsedAmount._hex,
            },
          ],
        });

        const transactionHash = await transactionsContract.addToBlockchain(
          addressTo,
          parsedAmount
        );

        // setIsLoading(true);
        console.log(`Loading - ${transactionHash.hash}`);
        await transactionHash.wait();
        console.log(`Success - ${transactionHash.hash}`);
        // setIsLoading(false);

        // const transactionsCount =
        //   await transactionsContract.getTransactionCount();

        // setTransactionCount(transactionsCount.toNumber());
        
        setFunded(true);
      } else {
        console.log("No ethereum object");
      }
    } catch (error) {
      console.log(error);

      throw new Error("No ethereum object");
    }
  };

  const [isFund, setIsFund] = useState(false);
  const [isAllowed, setIsAllowed] = useState(false);
  const btnRef = useRef();
  function fundCampaignHandler() {
    setIsFund(true);
    if (btnRef.current) {
      btnRef.current.setAttribute("disabled", "disabled");
    }
    if (typeof ethereum !== "undefined") {
      connectWallet();
    }
  }
  async  function handleList() {
    const transactionsContract = createEthereumContract();
    setIsAllowed(transactionsContract.checkIfFunder(currentAccount));
    console.log( await transactionsContract.getAllTransactions());
  }
  return (
    <div className="card-item">
      <div className="card-box table">
        <div className="card-preview">
          <h2> {props.ngo}</h2>
          <h4> {props.email}</h4>
          <h4> {props.phone}</h4>
        </div>
        <div className="card-info">
          <table>
            <tr>
              <td>
                <h3>
                  {" "}
                  Mission :
                  <td>
                    <u>{props.objective}</u>
                  </td>
                </h3>
              </td>
            </tr>
            <tr>
              <td>
                <h3>
                  {" "}
                  Minimum Acceptable Donation :
                  <td>
                    <u>{props.minimum}</u>
                  </td>
                </h3>
              </td>
            </tr>
            <tr>
              <td>
                <h3>
                  {" "}
                  Target Amount :
                  <td>
                    <u>{props.target}</u>
                  </td>
                </h3>
              </td>
            </tr>
            <tr>
              <td>
                <h3>
                  {" "}
                  MetaMask Wallet Address :
                  <td>
                    <u>{props.wallet}</u>
                  </td>
                </h3>
              </td>
            </tr>
          </table>
          <div className="container1">
            <div className="center1">
              <button
                className="fund-campaign-btn"
                onClick={fundCampaignHandler}
                ref={btnRef}
              >
                Fund Campaign
              </button>
            </div>
          </div>
          {isFund && (
            <form onSubmit={sendETH}>
              <div className="fund-campaign-div">
                <label className="fund-campaign-label">Donation Amount</label>
                <input
                  className="fund-campaign-input"
                  name="amount"
                  type="number"
                  placeholder="ETH"
                  min="0.0000"
                  step="0.0000001"
                  max="1009.00"
                  onChange={handleChange}
                />
                <div className="container2">
                  <div className="center2">
                    <button type="submit" className="fund-campaign-btn1">
                      Transfer
                    </button>
                  </div>
                </div>
              </div>
            </form>
          )}
          <div className="container2">
            <div className="center2">
              <button className="fund-campaign-btn1" onClick={handleList}>
                Transaction List
              </button>
              {/* {isAllowed && <Redirect to={`/track/${props.id}`} />} */}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};
export default CampaignCardItem;
