import React, { useEffect, useRef, useState } from "react";
import { ethers } from "ethers";
import { contractABI, contractAddress } from "../utils/constants";
import {
  statusABI,
  statusAddress,
} from "../fundersAndStatusContract/constants";
import "./CampaignCard.css";
import TrackTransactions from "../TrackTransactions";

const CampaignCardItem = (props) => {
  const { ethereum } = window;
  const [isLoading, setIsLoading] = useState(false);
  const [buttonText, setButtonText] = useState("TRANSFER ETH");
  const [currentAccount, setCurrentAccount] = useState("");
  const [formData, setformData] = useState("");
  const [isFund, setIsFund] = useState(false);
  const [isAllowed, setIsAllowed] = useState(false);
  const [campaignBalance, setCampaignBalance] = useState(0);
  const [noTransfer, setNoTransfer] = useState(false);
  const [transferDone, setTransferDone] = useState(false);
  const [amountRaised, setAmountRaised] = useState(0);
  const btnRef = useRef();
  const addressTo = props.wallet;
  const changeText = (text) => setButtonText(text);
  const createEthereumContract = () => {
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    const statusContract = new ethers.Contract(
      statusAddress,
      statusABI,
      signer
    );
    return statusContract;
  };

  const statusContract = createEthereumContract();

  useEffect(() => {
    fetch(
      `https://api-goerli.etherscan.io/api?module=account&action=balance&address=${props.wallet}&tag=latest&apikey=NV73WQ1VR3QCXY2K1WYGVRA1VHY8521D1V`
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setCampaignBalance(data.result);
      });
  }, []);

  const handleChange = (e) => {
    setformData(e.target.value);
  };
  ethereum.on("accountsChanged", function () {
    window.location.reload();
  });
  useEffect(() => {
    async function connectWallet() {
      if (typeof ethereum !== "undefined") {
        const accounts = await ethereum.request({
          method: "eth_requestAccounts",
        });
        setCurrentAccount(accounts[0]);
      }
    }
    connectWallet();
  }, [currentAccount, setCurrentAccount, ethereum]);

  const sendETH = async (e) => {
    e.preventDefault();

    try {
      if (ethereum) {
        const parsedAmount = ethers.utils.parseEther(formData);
        const parsedAmountInteger = parseInt(parsedAmount._hex, 16);
        console.log(parsedAmount._hex);
        // console.log(parseInt(parsedAmount._hex,16)/10000000000/100000000);
        await ethereum.request({
          method: "eth_sendTransaction",
          params: [
            {
              from: currentAccount,
              to: addressTo,
              gas: "0x5208",
              value: parsedAmount._hex,
            },
          ],
        });

        const statusHash = await statusContract.fund(
          addressTo,
          parsedAmountInteger
        );
        setIsLoading(true);
        await statusHash.wait();
        const total = await statusContract.getTotalDonation(addressTo);
        console.log(total._hex);
        setAmountRaised(parseInt(total,_hex,16)/10000000000/100000000);
        setIsLoading(false);
        changeText("TRANSFERRED");
        setTransferDone(true);
      } else {
        console.log("No ethereum object");
        setIsLoading(false);
      }
    } catch (error) {
      setIsLoading(false);
      console.log(error);
      throw new Error("No ethereum object");
    }
  };
  function fundCampaignHandler() {
    setIsFund(true);

    if (
      (parseInt(campaignBalance) / 10000000000 / 100000000).toFixed(6) ===
      props.target.toString()
    ) {
      setNoTransfer(true);
    }
  }

  async function handleList() {
    const enter = await statusContract.checkIfFunder(
      currentAccount,
      addressTo
    );
    setIsAllowed(enter);
    if (!enter) {
      alert("Not a Donor");
    }
  }
  function handleBack() {
    setIsAllowed(false);
  }
  return (
    <>
      {!isAllowed ? (
        <div className="outer">
          <div className="card-item">
            <div className="card-box table">
              <div className="card-info">
                <div className="card-preview">
                  <h2> {props.ngo}</h2>
                  <h4> {props.email}</h4>
                  <h4> {props.phone}</h4>

                  <hr />
                </div>
                <table className="table-content">
                  <tbody>
                    <tr>
                      <td className="table-entry-td"> Mission</td>
                      <td className="table-data-td">
                        <u>{props.objective} </u>
                      </td>
                    </tr>
                    <tr>
                      <td className="table-entry-td"> Minimum Donation</td>
                      <td className="table-data-td">
                        <u>{props.minimum} ETH</u>
                      </td>
                    </tr>
                    <tr>
                      <td className="table-entry-td"> Target Amount</td>
                      <td className="table-data-td">
                        <u>{props.target} ETH</u>
                      </td>
                    </tr>
                    <tr>
                      <td className="table-entry-td"> Wallet Address</td>
                      <td className="table-data-td">
                        <u>
                          {props.wallet.slice(0, 10)}
                          {"..."}
                          {props.wallet.slice(22, 32)}
                        </u>
                      </td>
                    </tr>

                    <tr>
                      <td className="table-entry-td">Current Balance</td>
                      <td className="table-data-td">
                        <u>
                          {(
                            parseInt(campaignBalance) /
                            10000000000 /
                            100000000
                          ).toFixed(6)}{" "}
                          ETH
                        </u>
                      </td>
                    </tr>
                    <tr>
                      <td className="table-entry-td">Amount Raised</td>
                      <td className="table-data-td">
                        <u>
                          {amountRaised}{" "}
                          ETH
                        </u>
                      </td>
                    </tr>
                  </tbody>
                </table>
                {!isFund && (
                  <button
                    className="variable-width-btn button-su fund-campaign-btn"
                    onClick={fundCampaignHandler}
                    ref={btnRef}
                  >
                    FUND CAMPAIGN
                  </button>
                )}
                {noTransfer && (
                  <>
                    <p className="done">TARGET REACHED. THANK YOU.</p>

                    <button
                      className="variable-width-btn button-su transaction-list-btn"
                      onClick={handleList}
                    >
                      TRANSACTION LIST
                    </button>
                  </>
                )}
                {isFund && !noTransfer && (
                  <form onSubmit={sendETH}>
                    {!transferDone && (
                      <input
                        className="fund-campaign-input"
                        name="amount"
                        type="number"
                        placeholder="ETH"
                        min={parseFloat(props.minimum)}
                        step={0.000001}
                        onChange={handleChange}
                      />
                    )}

                    {!isLoading && (
                      <button
                        type="submit"
                        className="variable-width-btn button-su fund-campaign-btn"
                      >
                        {buttonText}
                      </button>
                    )}
                  </form>
                )}
                {!isFund && (
                  <button
                    className="variable-width-btn button-su transaction-list-btn"
                    onClick={handleList}
                  >
                    TRANSACTION LIST
                  </button>
                )}
                {transferDone && (
                  <button
                    className="variable-width-btn button-su transaction-list-btn"
                    onClick={handleList}
                  >
                    TRANSACTION LIST
                  </button>
                )}
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div className="outer">
          <div className="separate">
            <button className="one-more button-su" onClick={handleBack}>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="16"
                height="16"
                fill="currentColor"
                class="bi bi-arrow-left"
                viewBox="0 0 16 16"
              >
                <path
                  fill-rule="evenodd"
                  d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8z"
                />
              </svg>
            </button>
            <TrackTransactions address={props.wallet} />
          </div>
        </div>
      )}
    </>
  );
};

export default CampaignCardItem;
