import React, { useState, useEffect } from "react";
import "./TrackTransactions.css";
import DateAndTime from "./Date.js";
import LoadingSpinner from "./LoadingSpinner";
function TrackTransactions(props) {
  const [isLoading, setIsLoading] = useState(false);
  const [apiData, setApiData] = useState([
    { from: "", to: "", value: "", timeStamp: "" },
  ]);
  function fetchTransactionData() {
    setIsLoading(true);
    fetch(
      `https://api-goerli.etherscan.io/api?module=account&action=txlist&address=${props.address}&startblock=0&endblock=99999999&page=1&offset=10&sort=desc&apikey=NV73WQ1VR3QCXY2K1WYGVRA1VHY8521D1V`
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setApiData(data.result);
        setIsLoading(false);
      });
  }
  useEffect(() => {
    fetchTransactionData();
  }, []);
  return (
    <div className="container6">
      <div className="outer6">
        {!isLoading && apiData.length >= 1 && (
          <div className="selected-NGO">
            <h1 className="selected">Transaction History</h1>

            {apiData.map((datum) => (
              <div className="api">
                {datum.from === props.address.toLowerCase() && (
                  <div className="internal">
                    <table className="unordered">
                      <tbody>
                        <tr>
                          <td className="tx-details">FROM:</td>
                          <td>{datum.from}</td>
                        </tr>
                        <tr>
                          <td className="tx-details">TO:</td>
                          <td>{datum.to}</td>
                        </tr>
                        <tr>
                          <td className="tx-details">VALUE:</td>
                          <td>
                            {(
                              parseInt(datum.value) /
                              10000000000 /
                              100000000
                            ).toString()}{" "}
                            ETH
                          </td>
                        </tr>
                        <tr>
                          <td className="tx-details">DATE/TIME:</td>
                          <td>{<DateAndTime time={datum.timeStamp} />}</td>
                        </tr>
                      </tbody>
                    </table>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
        {!isLoading && apiData.length === 0 && <p>NO DATA.</p>}
        {isLoading && <LoadingSpinner />}
      </div>
    </div>
  );
}
export default TrackTransactions;

