import { useEffect, useState } from "react";
import { escrowABI, escrowAddress } from "../escrowContract/constants";
import { ethers } from "ethers";
import CampaignList from "./CampaignList";
let allCampaigns;
const AllCampaigns = () => {
  const [isLoading, setIsLoading] = useState(true);
  useEffect(() => {
    (async () => {
      const { ethereum } = window;
      const createEscrowContract = () => {
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const escrowContract = new ethers.Contract(
          escrowAddress,
          escrowABI,
          signer
        );
        return escrowContract;
      };
      const escrowContract = createEscrowContract();
      allCampaigns = await escrowContract.getCampaigns();
      setIsLoading(false);
    })();
  }, []);

  return !isLoading && <CampaignList campaigns={allCampaigns} />;
};
export default AllCampaigns;
