import React, { useRef, useState } from "react";
import CampaignCards from "../CampaignCards";
import { Link } from "react-router-dom";
import "./CreateMyCampaignForm.css";
let cardDatum = {};
function CreateMyCampaignForm() {
  const [cardData, setCardData] = useState({});

  const [create, setCreate] = useState(false);
  const emailIdRef = useRef();
  const phoneNumberRef = useRef();
  const nameRef = useRef();
  const objectiveRef = useRef();
  const minimumDonationRef = useRef();
  const targetAmountRef = useRef();
  const walletAddressRef = useRef();
  const switchAuthModeHandler = () => {
    setCreate(true);
    setCardData(() => ({
      enteredEmail: emailIdRef.current.value,
      enteredPhoneNumber: phoneNumberRef.current.value,
      enteredName: nameRef.current.value,
      enteredObjective: objectiveRef.current.value,
      enteredMinimumDonation: minimumDonationRef.current.value,
      enteredTargetAmount: targetAmountRef.current.value,
      enteredWalletAddress: walletAddressRef.current.value,
    }));
    cardDatum = structuredClone(cardData);
    console.log("cardDatum");
    console.log(cardDatum);
  };
  const submitHandler = (event) => {
    event.preventDefault();
  };
  return (
    <section className="create-campaign">
      <h1>CREATE YOUR CAMPAIGN</h1>
      <form onSubmit={submitHandler}>
        <div className="control">
          <label htmlFor="infor-mail info-phone">Contact Information:</label>
          <input
            name="info-mail"
            type="email"
            placeholder="Email"
            ref={emailIdRef}
          ></input>
          <input
            name="info-phone"
            type="text"
            placeholder="Phone Number"
            ref={phoneNumberRef}
          ></input>
        </div>
        <div className="control">
          <label htmlFor="name-ngo-individual">Name (NGO/Individual):</label>
          <input name="name-ngo-individual" type="text" ref={nameRef}></input>
        </div>
        <div className="control">
          <label htmlFor="misson-objective">Mission/Objective:</label>
          <input name="misson-objective" type="text" ref={objectiveRef}></input>
        </div>
        <div className="control">
          <label htmlFor="minimum-donation">Minimum Donation:</label>
          <input
            name="minimum-donation"
            type="text"
            ref={minimumDonationRef}
          ></input>
        </div>
        <div className="control">
          <label htmlFor="target-amount">Target Amount:</label>
          <input name="target-amount" type="text" ref={targetAmountRef}></input>
        </div>
        <div className="control">
          <label htmlFor="metamask-wallet-address">
            MetaMask Wallet Address:
          </label>
          <input
            name="metamask-wallet-address"
            type="text"
            ref={walletAddressRef}
          ></input>
        </div>

        <div className="options">
          <button
            className="create-campaign-btn"
            type="submit"
            onSubmit={submitHandler}
            onClick={switchAuthModeHandler}
          >
            <Link to="myCard">Create Campaign</Link>
          </button>
        </div>
      </form>
      {create && (
        <Link to="myCard">
          <CampaignCards data={cardDatum} />
        </Link>
      )}
    </section>
  );
}

export default CreateMyCampaignForm;
