import React from "react";
import "./CampaignCard.css";
const CampaignCardItem = (props) => {
  return (
    <div>
      <div className="card-item">
        <div className="card-box table">
          <div className="card-preview">
            <h2> {props.ngo}</h2>
            <h3> {props.email}</h3>
            <h3> {props.phone}</h3>
          </div>
          <div className="card-info">
            <tbody>
              <tr>
                <td>
                  <h3> Objective :</h3>
                </td>
                <td>
                  <h3>{props.objective}</h3>
                </td>
              </tr>
              <tr>
                <td>
                  <h3> Minimum Acceptable Donation:</h3>
                </td>
                <td>
                  <h3>{props.minimum}</h3>
                </td>
              </tr>
              <tr>
                <td>
                  <h3> Target Amount:</h3>
                </td>
                <td>
                  <h3>{props.target}</h3>
                </td>
              </tr>
              <tr>
                <td>
                  <h3> MetaMask Wallet Address:</h3>
                </td>
                <td>
                  <h3>{props.wallet}</h3>
                </td>
              </tr>
            </tbody>
            <div className="container">
              <div className="center">
                <button class="fund-campaign-btn">Fund Campaign</button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CampaignCardItem;
