import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import { paymentABI, paymentAddress } from "..components/paymentContract/constants";
import "./TrackTransactions.css";
import DateAndTime from "./Date.js";
function TrackTransactions(props) {
  const { ethereum } = window;
  const [isLoading, setIsLoading] = useState(false);
  const [currentAccount, setCurrentAccount] = useState("");
  const [apiData, setApiData] = useState([
    { from: "", to: "", value: "", timeStamp: "" },
  ]);

  const createEthereumContract = () => {
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    const paymentContract = new ethers.Contract(
      paymentAddress,
      paymentABI,
      signer
    );
    return paymentContract;
  };

  const paymentContract = createEthereumContract();

  useEffect(() => {
    async function details() {
      const deets = await paymentContract.getDetails();
      console.log(deets);
    }
    details();
  }, []);

  ethereum.on("accountsChanged", function () {
    window.location.reload();
  });

  useEffect(() => {
    async function connectWallet() {
      if (typeof ethereum !== "undefined") {
        const accounts = await ethereum.request({
          method: "eth_requestAccounts",
        });
        setCurrentAccount(accounts[0]);
      }
    }
    connectWallet();
  }, [currentAccount, setCurrentAccount, ethereum]);

  function fetchTransactionData() {
    setIsLoading(true);
    fetch(
      `https://api-goerli.etherscan.io/api?module=account&action=txlist&address=${props.address}&startblock=0&endblock=99999999&page=1&offset=10&sort=desc&apikey=NV73WQ1VR3QCXY2K1WYGVRA1VHY8521D1V`
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setApiData(data.result);
        setIsLoading(false);
      });
  }
  useEffect(() => {
    fetchTransactionData();
  }, []);
  return (
    <div className="container6">
      <div className="outer6">
        {!isLoading && apiData.length >= 1 && (
          <div className="selected-NGO">
            <h1 className="selected">Transaction History</h1>

            {apiData.map((datum) => (
              <div className="api">
                {datum.from === props.address.toLowerCase() && (
                  <div className="internal">
                    <table className="unordered">
                      <tbody>
                        <tr>
                          <td className="tx-details">FROM:</td>
                          <td>{datum.from}</td>
                        </tr>
                        <tr>
                          <td className="tx-details">TO:</td>
                          <td>{datum.to}</td>
                        </tr>
                        <tr>
                          <td className="tx-details">VALUE:</td>
                          <td>
                            {(
                              parseInt(datum.value) /
                              10000000000 /
                              100000000
                            ).toString()}{" "}
                            ETH
                          </td>
                        </tr>
                        <tr>
                          <td className="tx-details">DATE/TIME:</td>
                          <td>{<DateAndTime time={datum.timeStamp} />}</td>
                        </tr>
                      </tbody>
                    </table>
                  </div>
                )}
              </div>
            ))}
          </div>
        )}
        {!isLoading && apiData.length === 0 && <p>NO DATA.</p>}
      </div>
    </div>
  );
}
export default TrackTransactions;

{
  /* <u>FROM:</u>
                      {datum.from} <br /> <u>TO:</u>
                      {datum.to} <br /> <u>VALUE:</u> {" "}
                      {(
                        parseInt(datum.value) /
                        10000000000 /
                        100000000
                      ).toString()}{" "}
                      ETH <br /> <u>DATE/TIME</u> :
                      {<DateAndTime time={datum.timeStamp} />}{" "}
                      */
}
