import { useHistory } from 'react-router-dom';

import CreateCampaignForm from './CreateCampaignForm';
import useHttp from './use-http';
import { addCampaign } from './api';

const NewCampaign = () => {
  const { sendRequest, status } = useHttp(addCampaign);
  const history = useHistory();

  // useEffect(() => {
  //   if (status === 'completed') {
  //     history.push('/homePage');
  //   }
  // }, [status, history]);

  const addCampaignHandler = (campaignData) => {
    sendRequest(campaignData);
  };

  return <CreateCampaignForm onAddQuote={addCampaignHandler} />;
};

export default NewCampaign;
