import React from "react";
import "./CampaignCard.css";
const CampaignCardItem = (props) => {
  return (
    <div className="card-item">
      <div className="card-box table">
        <div className="card-preview">
          <h2> {props.ngo}</h2>
          <h4> {props.email}</h4>
          <h4> {props.phone}</h4>
        </div>
        <div className="card-info">
          <table>
            <tr>
              <td>
                <h3>
                  {" "}
                  Mission :
                  <td>
                    <u>{props.objective}</u>
                  </td>
                </h3>
              </td>
            </tr>
            <tr>
              <td>
                <h3>
                  {" "}
                  Minimum Acceptable Donation :
                  <td>
                    <u>{props.minimum}</u>
                  </td>
                </h3>
              </td>
            </tr>
            <tr>
              <td>
                <h3>
                  {" "}
                  Target Amount :
                  <td>
                    <u>{props.target}</u>
                  </td>
                </h3>
              </td>
            </tr>
            <tr>
              <td>
                <h3>
                  {" "}
                  MetaMask Wallet Address :
                  <td>
                    <u>{props.wallet}</u>
                  </td>
                </h3>
              </td>
            </tr>
          </table>
          <div className="container">
            <div className="center">
              <button className="fund-campaign-btn">Fund Campaign</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CampaignCardItem;
