import React from "react";

import "./CampaignCard.css"
const CampaignCardItem = (props) => {
  return (
    <li className="card-item">
      <figure className="card-box">
        <blockquote className="ngoName">
          <p>NGO NAME :   {props.ngo}</p>
        </blockquote>
        <blockquote className="objective">
          <p>MISSION/OBJECTIVE :  {props.objective}</p>
        </blockquote>
        <blockquote className="minimum">
          <p>MINIMUM ACCEPTABLE DONATION :  {props.minimum}</p>
        </blockquote>
        <blockquote className="target">
          <p>TARGET :   {props.target}</p>
        </blockquote>
        <blockquote className="wallet">
          <p>METAMASK WALLET ADDDRESS :   {props.wallet}</p>
        </blockquote>
        <blockquote className="email">
          <p>CONTACT INFO : <br /> <br /> EMAIL :   {props.email}</p>
        </blockquote>
        <blockquote className="phone">
          <p>PHONE NUMBER :   {props.phone}</p>
        </blockquote>
      </figure>
      
    </li>
  );
};

export default CampaignCardItem;
