import React, { useRef, useState } from "react";
import CampaignCards from "../CampaignCards";
import { Link } from "react-router-dom";
import "./CreateMyCampaignForm.css";
let cardDatum = {};
function CreateMyCampaignForm() {
  const [create, setCreate] = useState(false);

  const [emailId, setEmailId] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [ngoName, setNgoName] = useState("");
  const [objective, setObjective] = useState("");
  const [minimumDonation, setMinimumDonation] = useState("");
  const [targetAmount, setTargetAmount] = useState("");
  const [walletAddress, setWalletAddress] = useState("");

  const [cardData, setCardData] = useState({
    enteredEmail: "",
    enteredPhoneNumber: "",
    enteredName: "",
    enteredObjective: "",
    enteredMinimumDonation: "",
    enteredTargetAmount: "",
    enteredWalletAddress: "",
  });

  const switchAuthModeHandler = () => {
    setCardData((prevState) => ({
      ...prevState,
      enteredEmail: emailIdRef.current.value,
      enteredPhoneNumber: phoneNumberRef.current.value,
      enteredName: nameRef.current.value,
      enteredObjective: objectiveRef.current.value,
      enteredMinimumDonation: minimumDonationRef.current.value,
      enteredTargetAmount: targetAmountRef.current.value,
      enteredWalletAddress: walletAddressRef.current.value,
    }));
    setCreate(true);
    cardDatum = structuredClone(cardData);
    console.log("cardDatum");
    console.log(cardDatum);
  };
  const submitHandler = (event) => {
    event.preventDefault();
  };
  return (
    <section className="create-campaign">
      <h1>CREATE YOUR CAMPAIGN</h1>
      <form onSubmit={submitHandler}>
        <div className="control">
          <label htmlFor="infor-mail info-phone">Contact Information:</label>
          <input
            name="info-mail"
            type="email"
            placeholder="Email"
            value={emailId}
            onChange={(e) => setEmailId(e.target.value)}
          ></input>
          <input
            name="info-phone"
            type="text"
            placeholder="Phone Number"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
          ></input>
        </div>
        <div className="control">
          <label htmlFor="name-ngo-individual">Name (NGO/Individual):</label>
          <input name="name-ngo-individual" type="text" value={ngoName} onChange={(e)=>setNgoName(e.target.value)}></input>
        </div>
        <div className="control">
          <label htmlFor="misson-objective">Mission/Objective:</label>
          <input name="misson-objective" type="text" value={objective} onChange={(e)=>setObjective(e.target.value)}></input>
        </div>
        <div className="control">
          <label htmlFor="minimum-donation">Minimum Donation:</label>
          <input
            name="minimum-donation"
            type="text"
            value={minimumDonation}
            onChange={(e)=>setMinimumDonation(e.target.value)}
          ></input>
        </div>
        <div className="control">
          <label htmlFor="target-amount">Target Amount:</label>
          <input name="target-amount" type="text" value={targetAmount} onChange={(e)=>setTargetAmount(e.target.value)}></input>
        </div>
        <div className="control">
          <label htmlFor="metamask-wallet-address">
            MetaMask Wallet Address:
          </label>
          <input
            name="metamask-wallet-address"
            type="text"
            value={walletAddress}
            onChange={(e)=>setWalletAddress(e.target.value)}
          ></input>
        </div>

        <div className="options">
          <button
            className="create-campaign-btn"
            type="submit"
            onSubmit={submitHandler}
            onClick={switchAuthModeHandler}
          >
            <Link to="myCard">Create Campaign</Link>
          </button>
        </div>
      </form>
      {create && (
        <Link to="myCard">
          <CampaignCards data={cardDatum} />
        </Link>
      )}
    </section>
  );
}

export default CreateMyCampaignForm;
