import React, { useEffect } from "react";
import useHttp from "./use-http";
import { getAllCampaigns } from "./api";
import LoadingSpinner from "../LoadingSpinner";
import SingleCampaign from "./SingleCampaign";
function GetCampaign() {
  //   let email;
  //   const getCurrentUser = async function () {
  //     const currentUser = await Parse.User.current();
  //     if (currentUser !== null) {
  //       email = currentUser.get("email");
  //     }
  //     return currentUser;
  //   };

  //   getCurrentUser();
//   var user = JSON.parse(localStorage.getItem("currentUser"));
//   console.log(user.username);
  const email = "graciousgiving@gmail.com";
  const {
    sendRequest,
    status,
    data: loadedCampaigns,
  } = useHttp(getAllCampaigns, true);
  useEffect(() => {
    sendRequest();
  }, [sendRequest]);

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }
  console.log(loadedCampaigns);
  const filteredCampaign = loadedCampaigns.filter((element) => {
    return element.email === email;
  });
  console.log(filteredCampaign);
  return (
    <div className="filter">
      <SingleCampaign campaigns={filteredCampaign} />
    </div>
  );
}
export default GetCampaign;
