import React, { useEffect } from "react";
import useHttp from "./use-http";
import { getAllCampaigns } from "./api";
// import { useParams } from "react-router-dom";
import LoadingSpinner from "../LoadingSpinner";
// import CampaignList from "./CampaignList";
function GetCampaign() {
  //   const params = useParams();
  //   const { campaignId } = params;
  const email = "graciousgiving@gmail.com";
  const {
    sendRequest,
    status,
    data: loadedCampaigns,
  } = useHttp(getAllCampaigns, true);
  useEffect(() => {
    sendRequest();
  }, [sendRequest]);

  if (status === "pending") {
    return (
      <div className="centered">
        <LoadingSpinner />
      </div>
    );
  }
  console.log(loadedCampaigns);

  //   return <CampaignList campaigns={loadedCampaigns} />;
  return <div>{loadedCampaigns.map((element) => {
    {console.log(element.email) && element.email===email && <h1>{email}</h1>}
  })};</div>;
}
export default GetCampaign;
