import React, { useState, useEffect } from "react";
import "./CampaignCard.css";
import LoadingSpinner from "../LoadingSpinner";
import { ethers } from "ethers";
import { Redirect } from "react-router-dom";
const SingleCard = (props) => {
  const [buttonText, setButtonText] = useState("Get Balance");
  const [isLoading, setIsLoading] = useState(false);
  const [LogOut, setLogOut] = useState(false);
  const [campaignBalance, setCampaignBalance] = useState(0);
  // const GetBalance = async () => {
  //   setIsLoading(true);
  //   const provider = new ethers.providers.Web3Provider(window.ethereum);
  //   const balance = await provider.getBalance(props.wallet);
  //   const balanceInEth = ethers.utils.formatEther(balance);
  //   setIsLoading(false);
  //   setButtonText(balanceInEth + " ETH");
  // };
  useEffect(() => {
    fetch(
      `https://api-goerli.etherscan.io/api?module=account&action=balance&address=${props.wallet}&tag=latest&apikey=NV73WQ1VR3QCXY2K1WYGVRA1VHY8521D1V`
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setCampaignBalance(data.result);
      });
  }, []);
  // function logOut() {
  //   localStorage.clear();
  //   setLogOut(true);
  // }
  
  return (
    <div className="single-outer">
      <div className="single-card-item">
        <div className="single-card-box table">
          <div className="single-card-preview">
            <h2> {props.ngo}</h2>
            <h4> {props.email}</h4>
            <h4> {props.phone}</h4>
          </div>
          <div className="single-card-info">
            <table className="single-table-content">
              <tbody>
                <tr>
                  <td className="single-table-entry-td"> Mission</td>
                  <td className="single-table-data-td">
                    <u>{props.objective} </u>
                  </td>
                </tr>
                <tr>
                  <td className="single-table-entry-td"> Minimum Donation</td>
                  <td className="single-table-data-td">
                    <u>{props.minimum} ETH</u>
                  </td>
                </tr>
                <tr>
                  <td className="single-table-entry-td"> Target Amount</td>
                  <td className="single-table-data-td">
                    <u>{props.target} ETH</u>
                  </td>
                </tr>
                <tr>
                  <td className="single-table-entry-td"> Wallet Address</td>
                  <td className="single-table-data-td">
                    <u>
                      {props.wallet.slice(0, 10)}
                      {"..."}
                      {props.wallet.slice(22, 32)}
                    </u>
                  </td>
                </tr>
                <tr>
                      <td className="single-table-entry-td">Current Balance</td>
                      <td className="single-table-data-td">
                        <u>
                          {(
                            parseInt(campaignBalance) /
                            10000000000 /
                            100000000
                          ).toFixed(6)}{" "}
                          ETH
                        </u>
                      </td>
                    </tr>
              </tbody>
            </table>
            {/* {!isLoading && (
              <button className="transaction-list-btn" onClick={GetBalance}>
                <span></span>
                <span></span>
                <span></span>
                <span></span>
                {buttonText}
              </button>
            )} */}
            {isLoading && <LoadingSpinner />}
            <button className="transaction-list-btn" onClick={logOut}>
              <span></span>
              <span></span>
              <span></span>
              <span></span>
              Log Out
            </button>
            {LogOut && <Redirect to="/landingPage" />}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SingleCard;
