import React, { useRef, useState } from "react";
import { Redirect } from "react-router-dom";
import "./SignUp.css";
function SignUpAsDonor() {
  const emailInputRef = useRef();
  const passwordInputRef = useRef();
  const addressInputRef = useRef();
  const [isSignUp, setIsSignUp] = useState(false);
  const submitHandler = (event) => {
    event.preventDefault();
    setIsSignUp(true);
    const enteredEmail = emailInputRef.current.value;
    const enteredPassword = passwordInputRef.current.value;
    const enteredAddress = addressInputRef.current.value;

    fetch(
      "https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyB0I1gvMRvmO1maRsFbDKMS1K_GPzNt1r0",
      {
        method: "POST",
        body: JSON.stringify({
          email: enteredEmail,
          password: enteredPassword,
          returnSecureToken: true,
        }),
        headers: { "Content-Type": "application/json" },
      }
    );

    fetch(
      "https://login-auth-640ec-default-rtdb.firebaseio.com/DonorWalletAddress.json",
      {
        method: "POST",
        body: JSON.stringify({
          email: enteredEmail,
          address: enteredAddress,
        }),
        headers: { "Content-Type": "application/json" },
      }
    );
  };

  return (
    <div className="container10">
      <form className="login" onSubmit={submitHandler}>
        <h1 className="head">DONOR SIGN UP</h1>
        <input
          type="text"
          className="login__input"
          placeholder="Email"
          required
          ref={emailInputRef}
        />
        <br />
        <hr />
        <input
          type="password"
          className="login__input"
          placeholder="Password"
          required
          ref={passwordInputRef}
        />
        <br />
        <hr />
        <input
          type="address"
          className="login__input"
          placeholder="MetaMask Wallet Address"
          required
          ref={addressInputRef}
        />
        <hr />
        <button className="button-su transaction-list-btn">SIGN UP</button>
      </form>
      {isSignUp && <Redirect to="/liD" />}
    </div>
  );
}

export default SignUpAsDonor;
