import React, { useRef, useState } from "react";
import { Redirect } from "react-router-dom";
import "./SignUpAsDonor.css";
function SignUpAsNGO() {
  const [create, setCreate] = useState(false);
  const emailInputRef = useRef();
  const passwordInputRef = useRef();
  const addressInputRef = useRef();
  const submitHandler = (event) => {
    event.preventDefault();
    setCreate(true);
    const enteredEmail = emailInputRef.current.value;
    const enteredPassword = passwordInputRef.current.value;
    const enteredAddress = addressInputRef.current.value;

    fetch(
      "https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyCWshObZRK4immwWy7uzJFMAyvsKO33kXA",
      {
        method: "POST",
        body: JSON.stringify({
          email: enteredEmail,
          password: enteredPassword,
          returnSecureToken: true,
        }),
        headers: { "Content-Type": "application/json" },
      }
    );

    fetch(
      "https://ngodata-7995b-default-rtdb.firebaseio.com/NGOWalletAddress.json",
      {
        method: "POST",
        body: JSON.stringify({
          email: enteredEmail,
          address: enteredAddress,
        }),
        headers: { "Content-Type": "application/json" },
      }
    );
  };

  return (
    <section className="sign-up-page">
      <h1>SIGN UP AS NGO/INDIVIDUAL</h1>
      <form onSubmit={submitHandler}>
        <div className="email control">
          <label htmlFor="email">Email</label>
          <input type="email" id="email" required ref={emailInputRef} />
        </div>
        <div className="password control">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            required
            ref={passwordInputRef}
          />
        </div>
        <div className="walletAddress control">
          <label htmlFor="walletAddress">MetaMask Wallet Address</label>
          <input type="text" id="address" required ref={addressInputRef} />
        </div>
        <div className="options">
          <button className="create-btn">Create Account</button>
        </div>
      </form>
      {create && <Redirect to="/cmcF" />}
    </section>
  );
}

export default SignUpAsNGO;
