import React, { useRef, useState } from "react";
import { ethers } from "ethers";
import ErrorMessage from "./ErrorMessage";
import TxList from "./TxList";
import "./CampaignCard.css";

const startPayment = async ({ setError, setTxs, ether, addr }) => {
  try {
    if (!window.ethereum) {
      throw new Error("No crypto wallet found. Please install it.");
    }
    await window.ethereum.send("eth_requestAccounts");
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();
    ethers.utils.getAddress(addr);
    const tx = await signer.sendTransaction({
      to: addr,
      value: ethers.utils.parseEther(ether),
    });
    console.log({ ether, addr });
    console.log("tx", tx);
    setTxs([tx]);
  } catch (err) {
    setError(err.message);
  }
};

const CampaignCardItem = (props) => {
  const [isFund, setIsFund] = useState(false);
  const [error, setError] = useState();
  const [txs, setTxs] = useState([]);
  const btnRef = useRef();
  function fundCampaignHandler() {
    setIsFund(true);
    if (btnRef.current) {
      btnRef.current.setAttribute("disabled", "disabled");
    }
  }
  const transferHandler = async (e) => {
    e.preventDefault();
    const data = new FormData(e.target);
    setError();
    await startPayment({
      setError,
      setTxs,
      ether: data.get("ether"),
      addr: data.get("addr"),
    });
  };
  return (
    <div className="card-item">
      <div className="card-box table">
        <div className="card-preview">
          <h2> {props.ngo}</h2>
          <h4> {props.email}</h4>
          <h4> {props.phone}</h4>
        </div>
        <div className="card-info">
          <table>
            <tr>
              <td>
                <h3>
                  {" "}
                  Mission :
                  <td>
                    <u>{props.objective}</u>
                  </td>
                </h3>
              </td>
            </tr>
            <tr>
              <td>
                <h3>
                  {" "}
                  Minimum Acceptable Donation :
                  <td>
                    <u>{props.minimum}</u>
                  </td>
                </h3>
              </td>
            </tr>
            <tr>
              <td>
                <h3>
                  {" "}
                  Target Amount :
                  <td>
                    <u>{props.target}</u>
                  </td>
                </h3>
              </td>
            </tr>
            <tr>
              <td>
                <h3>
                  {" "}
                  MetaMask Wallet Address :
                  <td>
                    <u>{props.wallet}</u>
                  </td>
                </h3>
              </td>
            </tr>
          </table>
          <div className="container1">
            <div className="center1">
              <button
                className="fund-campaign-btn"
                onClick={fundCampaignHandler}
                ref={btnRef}
              >
                Fund Campaign
              </button>
            </div>
          </div>
        </div>
      </div>
      {isFund && (
        <form onSubmit={transferHandler}>
          <div className="fund-campaign-div">
            <label className="fund-campaign-label">Donation Amount</label>
            <input
              className="fund-campaign-input"
              name="ether"
              type="text"
              placeholder="ETH"
            />
            <div className="container2">
              <div className="center2">
                <button type="submit" className="fund-campaign-btn1">
                  Transfer
                </button>
              </div>
            </div>
          </div>
        </form>
      )}
    </div>
  );
};

export default CampaignCardItem;

