const FIREBASE_DOMAIN = 'https://ngodata-7995b-default-rtdb.firebaseio.com';

export async function getAllCampaigns() {
  const response = await fetch(`${FIREBASE_DOMAIN}/Campaigns.json`);
  const data = await response.json();

  if (!response.ok) {
    throw new Error(data.message || 'Could not fetch campaigns.');
  }

  const transformedCampaigns = [];

  for (const key in data) {
    const campaignObj = {
      id: key,
      ...data[key],
    };

    transformedCampaigns.push(campaignObj);
  }

  return transformedCampaigns;
}

export async function getSingleCampaign(campaignId) {
  const response = await fetch(`${FIREBASE_DOMAIN}/Campaigns/${campaignId}.json`);
  const data = await response.json();

  if (!response.ok) {
    throw new Error(data.message || 'Could not fetch campaign.');
  }

  const loadedCampaign = {
    id: campaignId,
    ...data,
  };

  return loadedCampaign;
}

export async function addCampaign(campaignData) {
  const response = await fetch(`${FIREBASE_DOMAIN}/Campaigns.json`, {
    method: 'POST',
    body: JSON.stringify(campaignData),
    headers: {
      'Content-Type': 'application/json',
    },
  });
  const data = await response.json();

  if (!response.ok) {
    throw new Error(data.message || 'Could not create campaign.');
  }

  return null;
}

