import React, { useState, useEffect } from "react";
import "./CampaignCard.css";
function CampaignCards(props) {
  const [campaigns, setCampaigns] = useState(null);
  useEffect(() => {
    fetch("https://localhost:8000/campaigns")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data);
        setCampaigns(data);
      });
  }, []);
  return (
    <div className="whole">
      <div className="card">
        <div className="name-of-ngo">
          <h1>{props.enteredName}</h1>
        </div>
        <div className="mission">
          <h1>{props.enteredObjective}</h1>
        </div>
        <div className="target">
          <h1>{props.enteredTargetAmount}</h1>
        </div>
        <div className="minimum-donation target">
          <h1>{props.enteredMinimumDonation}</h1>
        </div>
        <div className="address target">
          <h1>{props.enteredWalletAddress}</h1>
        </div>
        <div className="container">
          <button className="fund-btn center">FUND CAMPAIGN</button>
        </div>
      </div>
    </div>
  );
}
export default CampaignCards;
