// BLOCKCHAIN DATABASE
import { useEffect, useState } from "react";
import { escrowABI, escrowAddress } from "../allInOneContract/constants";
import { ethers } from "ethers";
import Temporary from "./Temporary";
const GetCampaign = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [loadedCampaigns, setLoadedCampaigns] = useState([{}]);
  const { ethereum } = window;
  let count = 0;
  let allCampaigns;
  const start = async () => {
    const createEscrowContract = () => {
      const provider = new ethers.providers.Web3Provider(ethereum);
      const signer = provider.getSigner();
      const escrowContract = new ethers.Contract(
        escrowAddress,
        escrowABI,
        signer
      );
      return escrowContract;
    };
    const escrowContract = createEscrowContract();
    allCampaigns = await escrowContract.getCampaigns();
    count++;

    setIsLoading(false);
  };
  useEffect(() => {
    start();
  }, []);
  if (count !== 0) {
    setLoadedCampaigns(allCampaigns);
    console.log(allCampaigns);
    console.log(loadedCampaigns);
  }
  return <>{!isLoading && <Temporary campaigns={loadedCampaigns} />}</>;
};
export default GetCampaign;
