import React, { useState } from "react";
import "./CampaignCard.css";
import LoadingSpinner from "../LoadingSpinner";
import { ethers } from "ethers";
const SingleCard = (props) => {
  const [buttonText, setButtonText] = useState("Get Balance");
  const [isLoading, setIsLoading] = useState(false);
  const GetBalance = async () => {
    setIsLoading(true);
    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const balance = await provider.getBalance(props.wallet);
    const balanceInEth = ethers.utils.formatEther(balance);
    setIsLoading(false);
    setButtonText(balanceInEth + " ETH");

    // console.log(balanceInEth);
  };
  return (
    <div className="outer">
      <div className="card-item">
        <div className="card-box table">
          <div className="card-preview">
            <h2> {props.ngo}</h2>
            <h4> {props.email}</h4>
            <h4> {props.phone}</h4>
          </div>
          <div className="card-info">
            <table>
              <tr>
                <td>
                  <h3>
                    {" "}
                    Mission :
                    <td>
                      <u>{props.objective}</u>
                    </td>
                  </h3>
                </td>
              </tr>
              <tr>
                <td>
                  <h3>
                    {" "}
                    Minimum Acceptable Donation :
                    <td>
                      <u>{props.minimum} ETH</u>
                    </td>
                  </h3>
                </td>
              </tr>
              <tr>
                <td>
                  <h3>
                    {" "}
                    Target Amount :
                    <td>
                      <u>{props.target} ETH</u>
                    </td>
                  </h3>
                </td>
              </tr>
              <tr>
                <td>
                  <h3>
                    {" "}
                    MetaMask Wallet Address :
                    <td>
                      <u>{props.wallet}</u>
                    </td>
                  </h3>
                </td>
              </tr>
            </table>
            {!isLoading && <div className="container11">
              <div className="center11">
                <button className="fund-campaign-btn1" onClick={GetBalance}>
                  {buttonText}
                </button>
              </div>
            </div>}
            {isLoading && <LoadingSpinner />}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SingleCard;
