import { useEffect } from 'react';
import useHttp from './use-http';
import { getAllCampaigns} from "./api";
import CampaignList from './CampaignList';
const AllCampaigns = () => {
  const { sendRequest, status, data: loadedCampaigns, error } = useHttp(
    getAllCampaigns,
    true
  );

  useEffect(() => {
    sendRequest();
  }, [sendRequest]);
  

  if (status === 'completed' && (!loadedCampaigns || loadedCampaigns.length === 0)) {
    console.log("NOT FOUND");
  }
  return <CampaignList campaigns={loadedCampaigns} />;
};

export default AllCampaigns;


