import React from "react";

import "./CampaignCard.css";
const CampaignCardItem = (props) => {
  return (
    <li className="card-item">
      <div className="card-box table">
        <div className="card-preview">
          <h2> {props.ngo}</h2>
          <h3> {props.email}</h3>
          <h3> {props.phone}</h3>
        </div>
        <div className="card-info">
          <h2> Cause:{props.objective}</h2>
          <h3> Minimum Acceptable Donation:{props.minimum}</h3>
          <h3> Target Amount:{props.target}</h3>
          <h3> MetaMask Wallet Address:{props.wallet}</h3>
          <button class="btn">Fund Campaign</button>
        </div>
      </div>
    </li>
  );
};

export default CampaignCardItem;
