// BLOCKCHAIN DATABASE
import { useEffect, useState } from "react";
import { detailsABI, detailsAddress } from "../detailsContract/constants";
import { ethers } from "ethers";
import SingleCard from "./SingleCard"
let allCampaigns;
const GetCampaign = () => {
  const email = JSON.parse(localStorage.getItem("userEmail"));
  const [isLoading, setIsLoading] = useState(true);
  useEffect(() => {
    (async () => {
      const { ethereum } = window;
      const createEthereumContract = () => {
        const provider = new ethers.providers.Web3Provider(ethereum);
        const signer = provider.getSigner();
        const detailsContract = new ethers.Contract(
          detailsAddress,
          detailsABI,
          signer
        );
        return detailsContract;
      };
      const detailsContract = createEthereumContract();
      allCampaigns = await detailsContract.getCampaigns();
      console.log(allCampaigns);
      setIsLoading(false);
    })();
  }, []);
  const filteredCampaign = allCampaigns.filter((element) => {
    return element.email === email;
  });
  return (
    !isLoading && (
      <div className="filter">
        <SingleCard campaign={filteredCampaign} />
      </div>
    )
  );
};
export default GetCampaign;
