import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import { paymentABI, paymentAddress } from "./paymentContract/constants";
import DateAndTime from "./Date.js";
import "./TrackTransactions.css";
function TrackTransactions(props) {
  const { ethereum } = window;
  const [isLoading, setIsLoading] = useState(false);
  const [currentAccount, setCurrentAccount] = useState("");
  const [gateway, setGateway] = useState(false);
  const [apiTracking, setApiTracking] = useState(false);
  const [filteredData, setFilteredData] = useState([
    {
      from: "",
      to: "",
      vendorName: "",
      vendorLocation: "",
      date: "",
      amount: "",
    },
  ]);
  const [apiData, setApiData] = useState([
    { from: "", to: "", value: "", timeStamp: "" },
  ]);

  const createEthereumContract = () => {
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    const paymentContract = new ethers.Contract(
      paymentAddress,
      paymentABI,
      signer
    );
    return paymentContract;
  };

  const paymentContract = createEthereumContract();

  useEffect(() => {
    async function details() {
      const deets = await paymentContract.getDetails();
      const filteredDetail = deets.filter((element) => {
        return element.from === props.address.toLowerCase();
      });
      setFilteredData(filteredDetail);
    }
    details();
  }, []);

  ethereum.on("accountsChanged", function () {
    window.location.reload();
  });

  useEffect(() => {
    async function connectWallet() {
      if (typeof ethereum !== "undefined") {
        const accounts = await ethereum.request({
          method: "eth_requestAccounts",
        });
        setCurrentAccount(accounts[0]);
      }
    }
    connectWallet();
  }, [currentAccount, setCurrentAccount, ethereum]);

  function fetchTransactionData() {
    setIsLoading(true);
    fetch(
      `https://api-goerli.etherscan.io/api?module=account&action=txlist&address=${props.address}&startblock=0&endblock=99999999&page=1&offset=10&sort=desc&apikey=NV73WQ1VR3QCXY2K1WYGVRA1VHY8521D1V`
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setApiData(data.result);
        setIsLoading(false);
      });
  }
  useEffect(() => {
    fetchTransactionData();
  }, []);

  function handleGateway() {
    setGateway(true);
    setApiTracking(false);
  }
  function handleAPI() {
    setApiTracking(true);
    setGateway(false);
  }
  return (
    <>
      <h1 className="selected">Transaction History</h1>
      <div className="track-btns">
        <button
          className="variable-width-btn-track button-su api-tracking"
          onClick={handleAPI}
        >
          API TRACKING
        </button>
        <button
          className="variable-width-btn-track button-su gateway-tracking"
          onClick={handleGateway}
        >
          GATEWAY TRACKING
        </button>
      </div>
      {gateway && (
        <div className="container6">
          <div className="outer6">
            {!isLoading && filteredData.length >= 1 && (
              <div className="selected-NGO">
                {filteredData.map((datum) => (
                  <div className="api">
                    {datum.from === props.address.toLowerCase() && (
                      <div className="internal">
                        <table className="unordered">
                          <tbody>
                            <tr>
                              <td className="tx-details">FROM:</td>
                              <td>{datum.from}</td>
                            </tr>
                            <tr>
                              <td className="tx-details">TO:</td>
                              <td>{datum.to}</td>
                            </tr>
                            <tr>
                              <td className="tx-details">VENDOR NAME:</td>
                              <td>{datum.vendorName}</td>
                            </tr>
                            <tr>
                              <td className="tx-details">VENDOR LOCATION:</td>
                              <td>{datum.vendorLocation}</td>
                            </tr>
                            <tr>
                              <td className="tx-details">VALUE:</td>
                              <td>{parseFloat(datum.amount).toString()} ETH</td>
                            </tr>
                            <tr>
                              <td className="tx-details">DATE/TIME:</td>
                              <td>{datum.date}</td>
                            </tr>
                          </tbody>
                        </table>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
            {!isLoading && filteredData.length === 0 && <p>NO DATA.</p>}
          </div>
        </div>
      )}
      {apiTracking && (
        <div className="container6">
          <div className="outer6">
            {!isLoading && apiData.length >= 1 && (
              <div className="selected-NGO">
                {apiData.map((datum) => (
                  <div className="api">
                    {datum.from === props.address.toLowerCase() && (
                      <div className="internal">
                        <table className="unordered">
                          <tbody>
                            <tr>
                              <td className="tx-details">FROM:</td>
                              <td>{datum.from}</td>
                            </tr>
                            <tr>
                              <td className="tx-details">TO:</td>
                              <td>{datum.to}</td>
                            </tr>
                            <tr>
                              <td className="tx-details">VALUE:</td>
                              <td>
                                {(
                                  parseInt(datum.value) /
                                  10000000000 /
                                  100000000
                                ).toString()}{" "}
                                ETH
                              </td>
                            </tr>
                            <tr>
                              <td className="tx-details">DATE/TIME:</td>
                              <td>{<DateAndTime time={datum.timeStamp} />}</td>
                            </tr>
                          </tbody>
                        </table>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
            {!isLoading && apiData.length === 0 && <p>NO DATA.</p>}
          </div>
        </div>
      )}
    </>
  );
}
export default TrackTransactions;
