import React, { useRef, useState } from "react";
import { ethers } from "ethers";
import { contractABI, contractAddress } from "../utils/constants";
import "./CampaignCard.css";
import TrackTransactions from "../TrackTransactions";
import LoadingSpinner from "../LoadingSpinner";
const CampaignCardItem = (props) => {
  const { ethereum } = window;
  const [isLoading, setIsLoading] = useState(false);
  const [val, setVal] = useState(props.minimum);
  const [currentAccount, setCurrentAccount] = useState("");
  const [formData, setformData] = useState("");
  const [isFund, setIsFund] = useState(false);
  const [isAllowed, setIsAllowed] = useState(false);
  const btnRef = useRef();
  const addressTo = props.wallet;

  const createEthereumContract = () => {
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    const transactionsContract = new ethers.Contract(
      contractAddress,
      contractABI,
      signer
    );
    return transactionsContract;
  };

  const transactionsContract = createEthereumContract();

  const handleChange = (e) => {
    setformData(e.target.value);
    setVal(e.target.value);
  };

  async function connectWallet() {
    if (typeof ethereum !== "undefined") {
      const accounts = await ethereum.request({
        method: "eth_requestAccounts",
      });
      setCurrentAccount(accounts[0]);
    }
  }
  const sendETH = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    try {
      if (ethereum) {
        const parsedAmount = ethers.utils.parseEther(formData);
        await ethereum.request({
          method: "eth_sendTransaction",
          params: [
            {
              from: currentAccount,
              to: addressTo,
              gas: "0x5208",
              value: parsedAmount._hex,
            },
          ],
        });
        await transactionsContract.fund(addressTo);
        setIsLoading(false);
      } else {
        console.log("No ethereum object");
        setIsLoading(false);
      }
    } catch (error) {
      setIsLoading(false);
      console.log(error);
      throw new Error("No ethereum object");
    }
  };
  function fundCampaignHandler() {
    setIsFund(true);
    if (btnRef.current) {
      btnRef.current.setAttribute("disabled", "disabled");
    }
    if (typeof ethereum !== "undefined") {
      connectWallet();
    }
  }

  async function handleList() {
    connectWallet();
    setIsAllowed(
      await transactionsContract.checkIfFunder(currentAccount, addressTo)
    );
  }
  function minMaxValidationFunc(that, value) {
    let min = parseInt(that.getAttribute("min"));
    let max = parseInt(that.getAttribute("max"));
    let val = parseInt(value);
    if (val < min || isNaN(val)) {
      return min;
    } else if (val > max) {
      return max;
    } else {
      return val;
    }
  }
  return (
    <div className="outer">
      <div className="card-item">
        <div className="card-box table">
          <div className="card-preview">
            <h2> {props.ngo}</h2>
            <h4> {props.email}</h4>
            <h4> {props.phone}</h4>
          </div>
          <div className="card-info">
            <table>
              <tr>
                <td>
                  <h3>
                    {" "}
                    Mission :
                    <td>
                      <u>{props.objective}</u>
                    </td>
                  </h3>
                </td>
              </tr>
              <tr>
                <td>
                  <h3>
                    {" "}
                    Minimum Acceptable Donation :
                    <td>
                      <u>{props.minimum}</u>
                    </td>
                  </h3>
                </td>
              </tr>
              <tr>
                <td>
                  <h3>
                    {" "}
                    Target Amount :
                    <td>
                      <u>{props.target}</u>
                    </td>
                  </h3>
                </td>
              </tr>
              <tr>
                <td>
                  <h3>
                    {" "}
                    MetaMask Wallet Address :
                    <td>
                      <u>{props.wallet}</u>
                    </td>
                  </h3>
                </td>
              </tr>
            </table>
            <div className="container1">
              <div className="center1">
                <button
                  className="fund-campaign-btn"
                  onClick={fundCampaignHandler}
                  ref={btnRef}
                >
                  Fund Campaign
                </button>
              </div>
            </div>
            {isFund && (
              <form onSubmit={sendETH}>
                <div className="fund-campaign-div">
                  <label className="fund-campaign-label">Donation Amount</label>
                  <input
                    className="fund-campaign-input"
                    name="amount"
                    type="range"
                    placeholder="ETH"
                    min="0"
                    step="0.001"
                    value={setVal}
                    // min="0.0001"
                    // step="0.0000001"
                    // max="1009.00"
                    // min={props.minimum}
                    // step={props.minimum}
                    // max={props.target}
                    // step="any"
                    // min="0"
                    // max={props.target.toString()}
                    onChange={handleChange}
                  />
                  {!isLoading && (
                    <div className="container2">
                      <div className="center2">
                        <button type="submit" className="fund-campaign-btn1">
                          Transfer
                        </button>
                      </div>
                    </div>
                  )}
                  {isLoading && <LoadingSpinner />}
                </div>
              </form>
            )}
            <div className="container2">
              <div className="center2">
                <button className="fund-campaign-btn1" onClick={handleList}>
                  Transaction List
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      {isAllowed && (
        <div className="track">
          <TrackTransactions address={props.wallet} />
        </div>
      )}
    </div>
  );
};

export default CampaignCardItem;
