import React, { useState } from "react";
import "./TrackTransactions.css";
import DateAndTime from "./Date.js";
import "./APIData.css";
import { id } from "ethers/lib/utils";
function TrackTransactions() {
  const [apiData, setApiData] = useState([
    { namE: "", from: "", to: "", value: "", timeStamp: "" },
  ]);
  function fetchTransactionData() {
    fetch(
      "https://api.etherscan.io/api?module=account&action=txlist&address=0x98388199AeC4c7489b92b09C630fDb022102a429&startblock=0&startblock=0&page=1&offset=20&sort=desc&apikey=NV73WQ1VR3QCXY2K1WYGVRA1VHY8521D1V"
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setApiData(data.result);
      });
  }

  return (
    <div className="container">
      <div className="list-of-NGOs">
        <div className="contain">
          <button
            className="first NGO-btn center"
            onClick={fetchTransactionData}
          >
            NGO-1
          </button>
          {/* <button className="NGO-btn center">NGO-2</button>
          <button className="NGO-btn center">NGO-3</button>
          <button className="NGO-btn center">NGO-4</button>
          <button className="NGO-btn center">NGO-5</button> */}
        </div>
      </div>
      <div className="selected-NGO">
        {apiData.length > 1 && (
          <div>
            <h1 className="selected">TRANSACTION HISTORY</h1>
            <ul className="unordered">
              {apiData.map((datum) => (
                <li className="api">
                  FROM:<br /> {datum.from} <br /> TO: <br />{datum.to} <br /> VALUE: <br />{" "}
                  {(parseInt(datum.value) / 10000000000 / 100000000).toString()}{" "}
                  ETH <br /> DATE/TIME :{<DateAndTime time={datum.timeStamp} />}{" "}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}
export default TrackTransactions;
