import React, { useState, useRef } from "react";
import "./SignUpAsDonor.css";
function SignUpAsDonor() {
  const emailInputRef = useRef();
  const passwordInputRef = useRef();
  const addressInputRef = useRef();
  const [isLogin, setIsLogin] = useState(true);

  const switchAuthModeHandler = () => {
    setIsLogin((prevState) => !prevState);
  };
  const submitHandler = (event) => {
    event.preventDefault();
    const enteredEmail = emailInputRef.current.value;
    const enteredPassword = passwordInputRef.current.value;
    const enteredAddress = addressInputRef.current.value;
    if (isLogin) {
      fetch(
        "https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyB0I1gvMRvmO1maRsFbDKMS1K_GPzNt1r0",
        {
          method: "POST",
          body: JSON.stringify({
            email: enteredEmail,
            password: enteredPassword,
            returnSecureToken: true,
          }),
          headers: { "Content-Type": "application/json" },
        }
      );
    } else {
      fetch(
        "https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyB0I1gvMRvmO1maRsFbDKMS1K_GPzNt1r0",
        {
          method: "POST",
          body: JSON.stringify({
            email: enteredEmail,
            password: enteredPassword,
            returnSecureToken: true,
          }),
          headers: { "Content-Type": "application/json" },
        }
      );

      fetch(
        "https://login-auth-640ec-default-rtdb.firebaseio.com/DonorWalletAddress.json",
        {
          method: "POST",
          body: JSON.stringify({
            email: enteredEmail,
            address: enteredAddress,
          }),
          headers: { "Content-Type": "application/json" },
        }
      );
    }
  };

  return (
    <section className="sign-up-page">
      <h1>{isLogin ? "LOG IN" : "SIGN UP AS DONOR"}</h1>
      <form onSubmit={submitHandler}>
        <div className="email control">
          <label htmlFor="email">Email</label>
          <input type="email" id="email" required ref={emailInputRef} />
        </div>
        <div className="password control">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            required
            ref={passwordInputRef}
          />
        </div>
        {isLogin && (
          <div className="walletAddress control">
            <label htmlFor="walletAddress">MetaMask Wallet Address</label>
            <input type="text" id="address" required ref={addressInputRef} />
          </div>
        )}
        <div className="options">
          <button className="create-btn">
            {isLogin ? "Login" : "Create Account"}
          </button>
          <button
            type="button"
            className="toggle"
            onClick={switchAuthModeHandler}
          >
            {isLogin ? "Create New account" : "Login with existing account"}
          </button>
        </div>
      </form>
    </section>
  );
}

export default SignUpAsDonor;
