import React from "react";

import "./CampaignCard.css";
const CampaignCardItem = (props) => {
  return (
    <li className="card-item">
      <div className="card-box table">
        <div className="card-preview">
          <h2> {props.ngo}</h2>
          <h3> {props.email}</h3>
          <h3> {props.phone}</h3>
        </div>

        <tr>
          <td> OBJECTIVE</td>
          <td> {props.objective}</td>
        </tr>
        <tr>
          <td> MINIMUM ACCEPTABLE DONATION</td>
          <td> {props.minimum}</td>
        </tr>
        <tr>
          <td> TARGET</td>
          <td> {props.target}</td>
        </tr>
        <tr>
          <td> METAMASK WALLET ADDDRESS</td>
          <td> {props.wallet}</td>
        </tr>
        <tr>
          <td> CONTACT INFO</td>
        </tr>
      </div>
    </li>
  );
};

export default CampaignCardItem;
