import React, { useRef, useState } from "react";
import { ethers } from "ethers";
import "./CampaignCard.css";
const CampaignCardItem = (props) => {
  const [isFund, setIsFund] = useState(false);
  const btnRef = useRef();
  function fundCampaignHandler() {
    setIsFund(true);
    if (btnRef.current) {
      btnRef.current.setAttribute("disabled", "disabled");
    }
    requestAccount();
  }
  async function requestAccount() {
    if (typeof window.ethereum !== "undefined") {
      console.log("detected");
      const account = await window.ethereum.request({
        method: "eth_requestAccounts",
      });
      console.log(account);
      connectWalllet();
    }
  }
  async function connectWalllet() {
    if (typeof window.ethereum !== "undefined") {
      await requestAccount();
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      console.log(provider);
    }
  }
  return (
    <div className="card-item">
      <div className="card-box table">
        <div className="card-preview">
          <h2> {props.ngo}</h2>
          <h4> {props.email}</h4>
          <h4> {props.phone}</h4>
        </div>
        <div className="card-info">
          <table>
            <tr>
              <td>
                <h3>
                  {" "}
                  Mission :
                  <td>
                    <u>{props.objective}</u>
                  </td>
                </h3>
              </td>
            </tr>
            <tr>
              <td>
                <h3>
                  {" "}
                  Minimum Acceptable Donation :
                  <td>
                    <u>{props.minimum}</u>
                  </td>
                </h3>
              </td>
            </tr>
            <tr>
              <td>
                <h3>
                  {" "}
                  Target Amount :
                  <td>
                    <u>{props.target}</u>
                  </td>
                </h3>
              </td>
            </tr>
            <tr>
              <td>
                <h3>
                  {" "}
                  MetaMask Wallet Address :
                  <td>
                    <u>{props.wallet}</u>
                  </td>
                </h3>
              </td>
            </tr>
          </table>
          <div className="container1">
            <div className="center1">
              <button
                className="fund-campaign-btn"
                onClick={fundCampaignHandler}
                ref={btnRef}
              >
                Fund Campaign
              </button>
            </div>
          </div>
        </div>
      </div>
      {isFund && (
        <div className="fund-campaign-div">
          <label className="fund-campaign-label">Donation Amount</label>
          <input
            className="fund-campaign-input"
            type="text"
            placeholder="ETH"
          />
          <div className="container2">
            <div className="center2">
              <button
                className="fund-campaign-btn1"
                onClick={fundCampaignHandler}
              >
                Transfer
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CampaignCardItem;
