import React, { useState, useEffect } from "react";
import "./TrackTransactions.css";
import DateAndTime from "./Date.js";
import LoadingSpinner from "./LoadingSpinner";
function TrackTransactions(props) {
  console.log(props.address);
  const [isLoading, setIsLoading] = useState(false);
  const [apiData, setApiData] = useState([
    { from: "", to: "", value: "", timeStamp: "" },
  ]);
  function fetchTransactionData() {
    setIsLoading(true);
    fetch(
      `https://api-goerli.etherscan.io/api?module=account&action=txlist&address=${props.address}&startblock=0&endblock=99999999&page=1&offset=10&sort=desc&apikey=NV73WQ1VR3QCXY2K1WYGVRA1VHY8521D1V`
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setApiData(data.result);
        setIsLoading(false);
      });
  }
  useEffect(()=>{
    fetchTransactionData();
  },[])
  return (
    <div className="container6">
      <div className="outer6">
        {!isLoading && apiData.length >= 1 && (
          <div className="selected-NGO">
            <h1 className="selected">Transaction History of {props.name}</h1>
            <ul className="unordered">
              {apiData.map((datum) => (
                <li className="api">
                  FROM:
                  <br /> {datum.from} <br /> TO: <br />
                  {datum.to} <br /> VALUE: <br />{" "}
                  {(parseInt(datum.value) / 10000000000 / 100000000).toString()}{" "}
                  ETH <br /> DATE/TIME :{<DateAndTime time={datum.timeStamp} />}{" "}
                </li>
              ))}
            </ul>
          </div>
        )}
        {!isLoading && apiData.length === 0 && <p>NO DATA.</p>}
        {isLoading && <LoadingSpinner />}
      </div>
    </div>
  );
}
export default TrackTransactions;
// `https://api-goerli.etherscan.io/api?module=account&action=balancemulti&address=0x40aD91c7C7cA0a5C0cbcA3C2e93cD61C97659b04&tag=latest&apikey=NV73WQ1VR3QCXY2K1WYGVRA1VHY8521D1V`
