import React, { useState, useEffect } from "react";
import firebase from "./src/components/firebase";
import { campaignRef } from "./src/components/firebase";
import { Link, Redirect } from "react-router-dom";
import "./CreateMyCampaignForm.css";
function CreateMyCampaignForm() {
  const [emailId, setEmailId] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [ngoName, setNgoName] = useState("");
  const [objective, setObjective] = useState("");
  const [minimumDonation, setMinimumDonation] = useState("");
  const [targetAmount, setTargetAmount] = useState("");
  const [walletAddress, setWalletAddress] = useState("");
  const [create, setCreate] = useState(false);

  const submitHandler = (event) => {
    event.preventDefault();
    setCreate(true);
    const campaign = {
      emailId,
      phoneNumber,
      ngoName,
      objective,
      minimumDonation,
      targetAmount,
      walletAddress,
    };
    useEffect(() => {
      function callFunc() {
        campaignRef.set({
          emailId: campaign.emailId,
          phoneNumber: campaign.phoneNumber,
          ngoName: campaign.ngoName,
          objective: campaign.objective,
          minimumDonation: campaign.minimumDonation,
          targetAmount: campaign.targetAmount,
          walletAddress: campaign.walletAddress,
        });
      }
      callFunc();
    },[]);
    // fetch("https://ngodata-7995b-default-rtdb.firebaseio.com/Campaigns.json", {
    //   method: "POST",
    //   body: JSON.stringify(campaign),
    //   headers: { "Content-Type": "application/json" },
    // }).then(() => {
    //   console.log("campaign added");
    // });
  };
  return (
    <section className="create-campaign">
      <h1>CREATE YOUR CAMPAIGN</h1>
      <form onSubmit={submitHandler}>
        <div className="control">
          <label htmlFor="infor-mail info-phone">Contact Information:</label>
          <input
            name="info-mail"
            type="email"
            placeholder="Email"
            value={emailId}
            onChange={(e) => setEmailId(e.target.value)}
          ></input>
          <input
            name="info-phone"
            type="text"
            placeholder="Phone Number"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
          ></input>
        </div>
        <div className="control">
          <label htmlFor="name-ngo-individual">Name (NGO/Individual):</label>
          <input
            name="name-ngo-individual"
            type="text"
            value={ngoName}
            onChange={(e) => setNgoName(e.target.value)}
          ></input>
        </div>
        <div className="control">
          <label htmlFor="misson-objective">Mission/Objective:</label>
          <input
            name="misson-objective"
            type="text"
            value={objective}
            onChange={(e) => setObjective(e.target.value)}
          ></input>
        </div>
        <div className="control">
          <label htmlFor="minimum-donation">Minimum Donation:</label>
          <input
            name="minimum-donation"
            type="text"
            value={minimumDonation}
            onChange={(e) => setMinimumDonation(e.target.value)}
          ></input>
        </div>
        <div className="control">
          <label htmlFor="target-amount">Target Amount:</label>
          <input
            name="target-amount"
            type="text"
            value={targetAmount}
            onChange={(e) => setTargetAmount(e.target.value)}
          ></input>
        </div>
        <div className="control">
          <label htmlFor="metamask-wallet-address">
            MetaMask Wallet Address:
          </label>
          <input
            name="metamask-wallet-address"
            type="text"
            value={walletAddress}
            onChange={(e) => setWalletAddress(e.target.value)}
          ></input>
        </div>

        <div className="options">
          <button className="create-campaign-btn" type="submit">
            Create Campaign
          </button>
        </div>
      </form>
      {create && <Redirect to="/homePage" />}
    </section>
  );
}

export default CreateMyCampaignForm;

