import React from "react";
import { ethers } from "ethers";
import "./Vote.css";
function Vote() {
  async function voteHandler() {
    // async? because we don't want the metamask to pop up immediately when we run the website like duhh
    if (typeof window.ethereum !== "undefined") {
      //this is to see if the browser HAS metamask or not
      await window.ethereum.request({ method: "eth_requestAccounts" }); //this will make metamask pop up and ask us to connect to it
    }
  }

  async function vote() {
    const vote = 1;
    console.log(`Funding with ${vote}..`);
    if (typeof window.ethereum !== "undefined") {
      //what all things we need for funding?
      // we need a provider / connection to blockchain
      // signer / wallet / someone with some gas
      // contract that we are interacting with
      // ABI & address
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const contract = new ethers.Contract(address, abi, signer);
      try {
        const transactionResponse = await contract.fund({
          value: ethers.utils.parseEther(vote),
        });
      } catch (error) {
        console.log(error);
      }
    }
  }
  return (
    <div className="vote">
      <div className="withdrawal">
        <h1>WITHDRAWAL REQUEST : RECONSTRUCTION</h1>
      </div>
      <div className="your-vote">
        <h1>YOUR VOTE</h1>
      </div>
      <div className="container">
        <button className="vote-btns center" onClick={voteHandler}>APPROVE</button>
        <button className="vote-btns center">REJECT</button>
      </div>
    </div>
  );
}
export default Vote;
