import React, { useRef, useState } from "react";
import { Redirect } from "react-router-dom";
import "./SignUpAsDonor.css";
function SignUpAsNGO() {
  const [create, setCreate] = useState(false);
  const emailInputRef = useRef();
  const passwordInputRef = useRef();
  const addressInputRef = useRef();
  const submitHandler = (event) => {
    event.preventDefault();
    setCreate(true);
    const enteredEmail = emailInputRef.current.value;
    const enteredPassword = passwordInputRef.current.value;
    const enteredAddress = addressInputRef.current.value;

    fetch(
      "https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyCWshObZRK4immwWy7uzJFMAyvsKO33kXA",
      {
        method: "POST",
        body: JSON.stringify({
          email: enteredEmail,
          password: enteredPassword,
          returnSecureToken: true,
        }),
        headers: { "Content-Type": "application/json" },
      }
    );

    fetch(
      "https://ngodata-7995b-default-rtdb.firebaseio.com/NGOWalletAddress.json",
      {
        method: "POST",
        body: JSON.stringify({
          email: enteredEmail,
          address: enteredAddress,
        }),
        headers: { "Content-Type": "application/json" },
      }
    );
  };

  return (
    <div className="container">
      <div className="screen">
        <div className="screen__content">
        
          <form className="login" onSubmit={submitHandler}>
            <div className="login__field">
              <i className="login__icon fas fa-user"></i>
              <input type="text" className="login__input" placeholder="Email" required ref={emailInputRef} defaultValue="1"/>
            </div>
            <div className="login__field">
              <i className="login__icon fas fa-lock"></i>
              <input
                type="password"
                className="login__input"
                placeholder="Password"
                required
                ref={passwordInputRef}
                defaultValue=""
              />
            </div>
            <div className="login__field">
              <i className="login__icon fas fa-lock"></i>
              <input
                type="address"
                className="login__input"
                placeholder="MetaMask Wallet Address"
                required
                ref={addressInputRef}
                defaultValue=""
              />
            </div>
            <button className="button login__submit">
              <span className="button__text">Sign Up </span>
            </button>
          </form>
        </div>
      </div>
      {create && <Redirect to="/cmcF" />}
    </div>
  );
}

export default SignUpAsNGO;
