import { useRef } from "react";
import "./CreateMyCampaignForm.css";
const CreateCampaignForm = (props) => {
  const emailInputRef = useRef();
  const phoneInputRef = useRef();
  const ngoNameRef = useRef();
  const objectiveRef = useRef();
  const minimumDonationRef = useRef();
  const targetAmountRef = useRef();
  const walletAddressRef = useRef();

  function submitHandler(event) {
    event.preventDefault();
    const enteredEmail = emailInputRef.current.value;
    const enteredPhone = phoneInputRef.current.value;
    const enteredNGO = ngoNameRef.current.value;
    const enteredObjective = objectiveRef.current.value;
    const enteredMinimum = minimumDonationRef.current.value;
    const enteredTarget = targetAmountRef.current.value;
    const enteredWallet = walletAddressRef.current.value;
    props.onAddQuote({
      email: enteredEmail,
      phone: enteredPhone,
      ngo: enteredNGO,
      objective: enteredObjective,
      minimum: enteredMinimum,
      target: enteredTarget,
      wallet: enteredWallet,
    });
  }

  return (
    <div className="container10">
      <section className="create-campaign">
        <h1>CREATE YOUR CAMPAIGN</h1>
        <form onSubmit={submitHandler}>
          <div className="control">
            <label htmlFor="info-mail info-phone">Contact Information:</label>
            <input
              id="info-mail"
              className="contact"
              type="email"
              placeholder="Email"
              required
              ref={emailInputRef}
            ></input>
            <br />
            <input
              id="info-phone"
              type="text"
              className="contact"
              placeholder="Phone Number"
              required
              ref={phoneInputRef}
            ></input>
          </div>
          <div className="control">
            {/* <label htmlFor="name-ngo-individual">Name (NGO/Individual):</label> */}
            <input
              id="name-ngo-individual"
              type="text"
              className="NGO"
              placeholder="Name (NGO/Individual):"
              required
              ref={ngoNameRef}
            ></input>
          </div>
          <div className="control">
            <label htmlFor="misson-objective">Mission/Objective:</label>
            <input
              id="misson-objective"
              type="text"
              className="objective"
              placeholder="Mission/Objective"
              required
              ref={objectiveRef}
            ></input>
          </div>
          <div className="control">
            <label htmlFor="minimum-donation">Minimum Donation:</label>
            <input
              id="minimum-donation"
              type="number"
              className="min"
              required
              ref={minimumDonationRef}
            ></input>
          </div>
          <div className="control">
            <label htmlFor="target-amount">Target Amount:</label>
            <input
              id="target-amount"
              type="number"
              className="target"
              required
              ref={targetAmountRef}
            ></input>
          </div>
          <div className="control">
            <label htmlFor="metamask-wallet-address">
              MetaMask Wallet Address:
            </label>
            <input
              id="metamask-wallet-address"
              type="address"
              className="waddress"
              required
              ref={walletAddressRef}
            ></input>
          </div>

          <div className="options">
            <button className="create-campaign-btn" type="submit">
              Create Campaign
            </button>
          </div>
        </form>
      </section>
    </div>
  );
};

export default CreateCampaignForm;
