import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import { escrowABI, escrowAddress } from "./escrowContract/constants";
import "./TrackTransactions.css";
function TrackTransactions(props) {
  const { ethereum } = window;
  const [currentAccount, setCurrentAccount] = useState("");
  const [filteredData, setFilteredData] = useState([
    {
      from: "",
      to: "",
      vendorName: "",
      vendorLocation: "",
      date: "",
      amount: "",
    },
  ]);

  const createEscrowContract = () => {
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    const escrowContract = new ethers.Contract(
      escrowAddress,
      escrowABI,
      signer
    );
    return escrowContract;
  };
  const escrowContract = createEscrowContract();

  useEffect(() => {
    async function details() {
      const deets = await escrowContract.getDetails();
      const filteredDetail = deets.filter((element) => {
        return element.from === props.address.toLowerCase();
      });
      const bc = Object.entries(filteredDetail);
      console.log(bc);
      bc.reverse();
      console.log(bc);
      setFilteredData(bc);
    }
    details();
  }, []);

  ethereum.on("accountsChanged", function () {
    window.location.reload();
  });

  useEffect(() => {
    async function connectWallet() {
      if (typeof ethereum !== "undefined") {
        const accounts = await ethereum.request({
          method: "eth_requestAccounts",
        });
        setCurrentAccount(accounts[0]);
      }
    }
    connectWallet();
  }, [currentAccount, setCurrentAccount, ethereum]);

  return (
    <>
      <h1 className="selected">Transaction History</h1>
      <div className="container6">
        <div className="outer6">
          {console.log(filteredData)}
          {filteredData.length >= 1 && (
            <div className="selected-NGO">
              {filteredData.map((datum) => (
                <div className="api">
                  {console.log(datum[1])}
                  {datum[1].from === props.address.toLowerCase() && (
                    <div className="internal">
                      <table className="unordered">
                        <tbody>
                          <tr>
                            <td className="tx-details">FROM:</td>
                            <td>{datum[1].from}</td>
                          </tr>
                          <tr>
                            <td className="tx-details">TO:</td>
                            <td>{datum[1].to}</td>
                          </tr>
                          <tr>
                            <td className="tx-details">VENDOR NAME:</td>
                            <td>{datum[1].vendorName}</td>
                          </tr>
                          <tr>
                            <td className="tx-details">VENDOR LOCATION:</td>
                            <td>{datum[1].vendorLocation}</td>
                          </tr>
                          <tr>
                            <td className="tx-details">VALUE:</td>
                            <td>
                              {parseFloat(datum[1].amount).toString()} ETH
                            </td>
                          </tr>
                          <tr>
                            <td className="tx-details">DATE/TIME:</td>
                            <td>{datum[1].date}</td>
                          </tr>
                        </tbody>
                      </table>
                    </div>
                  )}
                </div>
              ))}
            </div>
          )}
          {filteredData.length === 0 && <p className="no-data">NO DATA</p>}
        </div>
      </div>
    </>
  );
}
export default TrackTransactions;
