import { useEffect, useState } from "react";
import { detailsABI, detailsAddress } from "../detailsContract/constants";
import { ethers } from "ethers";
import SingleCard from "./SingleCard";
let allCampaigns;
let filteredCampaign;
const { ethereum } = window;
const createEthereumContract = () => {
  const provider = new ethers.providers.Web3Provider(ethereum);
  const signer = provider.getSigner();
  const detailsContract = new ethers.Contract(
    detailsAddress,
    detailsABI,
    signer
  );
  return detailsContract;
};
const GetCampaign = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [filteredCampaign, setFilteredCampaign] = useState({});
  const email = JSON.parse(localStorage.getItem("userEmail"));
  useEffect(() => {
    (async () => {
      const detailsContract = createEthereumContract();
      allCampaigns = await detailsContract.getCampaigns();
      // console.log(allCampaigns);
      setIsLoading(false);
      // filteredCampaign = allCampaigns.filter((element) => {
      //   return element.email === email;
      // });
      setFilteredCampaign(
        allCampaigns.filter((element) => {
          return element.email === email;
        })
      );
    })();
  });
  // if (!isLoading && typeof allCampaigns !== "undefined") {
  //   console.log(typeof allCampaigns);
  //   filteredCampaign = allCampaigns.filter((element) => {
  //     return element.email === email;
  //   });
  // }

  return (
    !isLoading &&
    typeof filteredCampaign !== "undefined" && (
      <div className="filter">
        <SingleCard campaign={filteredCampaign} />
      </div>
    )
  );
};
export default GetCampaign;
