import React, { useState } from "react";
import Data from "./components/APIData";
// import Navbar from "./components/Navbar";
// import Vote from "./components/Vote";
// import CampaignCards from "./components/CampaignCards";
// import Heading from "./components/Heading";
import "./App.css";
// import TrackTransactions from "./components/TrackTransactions";
function App() {
  const [apiData, setApiData] = useState([{ from: "", to: "", value: "" }]);
  function fetchTransactionData() {
    fetch(
      "https://api.etherscan.io/api?module=account&action=txlist&address=0x98388199AeC4c7489b92b09C630fDb022102a429&startblock=0&startblock=0&page=1&offset=10&sort=asc&apikey=NV73WQ1VR3QCXY2K1WYGVRA1VHY8521D1V"
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        const listItem = data.result.map((element) => {
          return { from: element.from, to: element.to, value: element.value };
        });
        // console.log(listItem);
        setApiData(listItem);
      });
  }

  return (
    <div className="App">
      <section>
        <button onClick={fetchTransactionData}>GET TRANSACTIONS</button>
      </section>
      <Data apiData={apiData} />
    </div>
  );
}
export default App;
