import React, { useState, useEffect } from "react";
import "./CampaignCard.css";
import { Redirect } from "react-router-dom";
const SingleCard = (props) => {
  console.log(props.campaigns[0].email);
  const [LogOut, setLogOut] = useState(false);
  const [campaignBalance, setCampaignBalance] = useState(0);
  useEffect(() => {
    fetch(
      `https://api-goerli.etherscan.io/api?module=account&action=balance&address=${props.campaigns[0].wallet}&tag=latest&apikey=NV73WQ1VR3QCXY2K1WYGVRA1VHY8521D1V`
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setCampaignBalance(data.result);
      });
  }, []);
  function logOut() {
    localStorage.clear();
    setLogOut(true);
  }


  return (
    <div className="single-outer">
      <div className="single-card-item">
        <div className="single-card-box table">
          <div className="single-card-preview">
            <h2> {props.campaigns[0].ngo}</h2>
            <h4> {props.campaigns[0].email}</h4>
            <h4> {props.campaigns[0].phone}</h4>
            <hr />
          </div>
          <div className="single-card-info">
            <table className="single-table-content">
              <tbody>
                <tr>
                  <td className="single-table-entry-td"> Mission</td>
                  <td className="single-table-data-td">
                    <u>{props.campaigns[0].objective} </u>
                  </td>
                </tr>
                <tr>
                  <td className="single-table-entry-td"> Minimum Donation</td>
                  <td className="single-table-data-td">
                    <u>{props.campaigns[0].minimum} ETH</u>
                  </td>
                </tr>
                <tr>
                  <td className="single-table-entry-td"> Target Amount</td>
                  <td className="single-table-data-td">
                    <u>{props.campaigns[0].target} ETH</u>
                  </td>
                </tr>
                <tr>
                  <td className="single-table-entry-td"> Wallet Address</td>
                  <td className="single-table-data-td">
                    <u>
                      {props.campaigns[0].wallet.slice(0, 10)}
                      {"..."}
                      {props.campaigns[0].wallet.slice(22, 32)}
                    </u>
                  </td>
                </tr>
                <tr>
                  <td className="single-table-entry-td">Current Balance</td>
                  <td className="single-table-data-td">
                    <u>
                      {(
                        parseInt(campaignBalance) /
                        10000000000 /
                        100000000
                      ).toFixed(6)}{" "}
                      ETH
                    </u>
                  </td>
                </tr>
              </tbody>
            </table>
            <button
              className="variable-width-btn button-su transaction-list-btn"
              onClick={logOut}
            >
              LOG OUT
            </button>
            {LogOut && <Redirect to="/landingPage" />}
          </div>
        </div>
      </div>
    </div>
  );
};

export default SingleCard;
