import React, { useState, useEffect } from "react";
import {
  withdrawalABI,
  withdrawalAddress,
} from "../withdrawalContract/constants";
import { ethers } from "ethers";
import "./CampaignCard.css";
import { Redirect } from "react-router-dom";
import RequestsPage from "./RequestsPage";
const SingleCard = (props) => {
  // console.log(props.campaigns[0].email);
  const { ethereum } = window;
  const [LogOut, setLogOut] = useState(false);
  const [campaignBalance, setCampaignBalance] = useState(0);
  const [request, setRequest] = useState(false);
  const [view, setView] = useState(false);
  const [allRequests, setAllRequests] = useState(Object);
  const [publish, setPublish] = useState(false);
  const [formData, setformData] = useState("");

  const createEthereumContract = () => {
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    const withdrawalContract = new ethers.Contract(
      withdrawalAddress,
      withdrawalABI,
      signer
    );
    return withdrawalContract;
  };
  const withdrawalContract = createEthereumContract();
  useEffect(() => {
    fetch(
      `https://api-goerli.etherscan.io/api?module=account&action=balance&address=${props.campaigns[0].wallet}&tag=latest&apikey=NV73WQ1VR3QCXY2K1WYGVRA1VHY8521D1V`
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setCampaignBalance(data.result);
      });
  }, []);
  function logOut() {
    localStorage.clear();
    setLogOut(true);
  }
  const handleChange = (e) => {
    setformData(e.target.value);
  };
  function handleRequestClick() {
    setRequest(true);
  }
  const handleViewRequest = async () => {
    const all = await withdrawalContract.allRequests();
    setAllRequests(all);
    setView(true);
  };
  const handleRequest = async (e) => {
    e.preventDefault();
    const withdrawalHash = await withdrawalContract.makeRequest(
      props.campaigns[0].wallet,
      formData
    );
    await withdrawalHash.wait();

    // console.log(all);
    // console.log(typeof all);
    setPublish(true);
  };
  function handleBack() {
    setView(false);
  }
  return (
    <>
      {!view ? (
        <div className="single-outer">
          <div className="single-card-item">
            <div className="single-card-box table">
              <div className="single-card-preview">
                <h2> {props.campaigns[0].ngo}</h2>
                <h4> {props.campaigns[0].email}</h4>
                <h4> {props.campaigns[0].phone}</h4>
                <hr />
              </div>
              <div className="single-card-info">
                <table className="single-table-content">
                  <tbody>
                    <tr>
                      <td className="single-table-entry-td"> Mission</td>
                      <td className="single-table-data-td">
                        <u>{props.campaigns[0].objective} </u>
                      </td>
                    </tr>
                    <tr>
                      <td className="single-table-entry-td">
                        {" "}
                        Minimum Donation
                      </td>
                      <td className="single-table-data-td">
                        <u>{props.campaigns[0].minimum} ETH</u>
                      </td>
                    </tr>
                    <tr>
                      <td className="single-table-entry-td"> Target Amount</td>
                      <td className="single-table-data-td">
                        <u>{props.campaigns[0].target} ETH</u>
                      </td>
                    </tr>
                    <tr>
                      <td className="single-table-entry-td"> Wallet Address</td>
                      <td className="single-table-data-td">
                        <u>
                          {props.campaigns[0].wallet.slice(0, 10)}
                          {"..."}
                          {props.campaigns[0].wallet.slice(22, 32)}
                        </u>
                      </td>
                    </tr>
                    <tr>
                      <td className="single-table-entry-td">Current Balance</td>
                      <td className="single-table-data-td">
                        <u>
                          {(
                            parseInt(campaignBalance) /
                            10000000000 /
                            100000000
                          ).toFixed(6)}{" "}
                          ETH
                        </u>
                      </td>
                    </tr>
                  </tbody>
                </table>
                {!request && (
                  <button
                    className="variable-width-btn button-su transaction-list-btn"
                    onClick={handleRequestClick}
                  >
                    MAKE A WITHDRAWAL REQUEST
                  </button>
                )}
                {request && (
                  <form onSubmit={handleRequest}>
                    <input
                      className="fund-campaign-input"
                      name="request"
                      type="text"
                      autoComplete="off"
                      placeholder="Withdrawal Request"
                      onChange={handleChange}
                    />
                    {!publish && (
                      <button
                        type="submit"
                        className="variable-width-btn button-su fund-campaign-btn"
                      >
                        PUBLISH REQUEST
                      </button>
                    )}
                  </form>
                )}
                {!view && (
                  <button
                    className="variable-width-btn button-su fund-campaign-btn"
                    onClick={handleViewRequest}
                  >
                    VIEW WITHDRAWAL REQUESTS
                  </button>
                )}
                {console.log(allRequests)}

                <button
                  className="variable-width-btn button-su transaction-list-btn"
                  onClick={logOut}
                >
                  LOG OUT
                </button>
                {LogOut && <Redirect to="/landingPage" />}
              </div>
            </div>
          </div>
        </div>
      ) : (
        <div className="single-outer-req">
          <div className="single-card-item-req">
            <button className="one-more button-su" onClick={handleBack}>
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="16"
                height="16"
                fill="currentColor"
                class="bi bi-arrow-left"
                viewBox="0 0 16 16"
              >
                <path
                  fill-rule="evenodd"
                  d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8z"
                />
              </svg>
            </button>
            <RequestsPage allRequests={allRequests} address ={props.campaigns[0].wallet}/>
          </div>
        </div>
      )}
    </>
  );
};

export default SingleCard;
// {view && }
