import React, { useRef} from "react";
import "./SignUpAsDonor.css";
function SignUpAsDonor() {
  const emailInputRef = useRef();
  const passwordInputRef = useRef();
  const addressInputRef = useRef();

  const submitHandler = (event) => {
    event.preventDefault();
    const enteredEmail = emailInputRef.current.value;
    const enteredPassword = passwordInputRef.current.value;
    const enteredAddress = addressInputRef.current.value;

    fetch(
      "https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyB0I1gvMRvmO1maRsFbDKMS1K_GPzNt1r0",
      {
        method: "POST",
        body: JSON.stringify({
          email: enteredEmail,
          password: enteredPassword,
          returnSecureToken: true,
        }),
        headers: { "Content-Type": "application/json" },
      }
    );

    fetch(
      "https://login-auth-640ec-default-rtdb.firebaseio.com/DonorWalletAddress.json",
      {
        method: "POST",
        body: JSON.stringify({
          email: enteredEmail,
          address: enteredAddress,
        }),
        headers: { "Content-Type": "application/json" },
      }
    );
  };

  return (
    <div className="container">
      <div className="screen">
        <div className="screen__content">
        
          <form className="login" onSubmit={submitHandler}>
            <div className="login__field">
              <i className="login__icon fas fa-user"></i>
              <input type="text" className="login__input" placeholder="Email" required ref={emailInputRef}/>
            </div>
            <div className="login__field">
              <i className="login__icon fas fa-lock"></i>
              <input
                type="password"
                className="login__input"
                placeholder="Password"
                required
                ref={passwordInputRef}
              />
            </div>
            <div className="login__field">
              <i className="login__icon fas fa-lock"></i>
              <input
                type="address"
                className="login__input"
                placeholder="MetaMask Wallet Address"
                required
                ref={addressInputRef}
              />
            </div>
            <button className="button login__submit">
              <span className="button__text">Sign Up </span>
            </button>
          </form>
        </div>
        <div className="screen__background">
          <span className="screen__background__shape screen__background__shape4"></span>
          <span className="screen__background__shape screen__background__shape3"></span>
          <span className="screen__background__shape screen__background__shape2"></span>
          <span className="screen__background__shape screen__background__shape1"></span>
        </div>
      </div>
    </div>
  );
}

export default SignUpAsDonor;
