import React, { useState } from "react";
import "./TrackTransactions.css";
// import { addr } from "./createCampaign/CampaignCardItem";
import DateAndTime from "./Date.js";
import LoadingSpinner from "./LoadingSpinner";
function TrackTransactions(props) {
  // console.log(addr);
  const addre = props;
  console.log(addre);
  const [isLoading, setIsLoading] = useState(false);
  const [apiData, setApiData] = useState([
    { from: "", to: "", value: "", timeStamp: "" },
  ]);
  function fetchTransactionData() {
    setIsLoading(true);
    fetch(
      // `https://api-goerli.etherscan.io/api?module=account&action=balancemulti&address=0x40aD91c7C7cA0a5C0cbcA3C2e93cD61C97659b04&tag=latest&apikey=NV73WQ1VR3QCXY2K1WYGVRA1VHY8521D1V`
      // "https://api.etherscan.io/api?module=account&action=txlist&address=0x98388199AeC4c7489b92b09C630fDb022102a429&startblock=0&startblock=0&page=1&offset=10&sort=asc&apikey=NV73WQ1VR3QCXY2K1WYGVRA1VHY8521D1V"
    `https://api-goerli.etherscan.io/api?module=account&action=txlist&address=${addre}&startblock=0&endblock=99999999&page=1&offset=10&sort=desc&apikey=NV73WQ1VR3QCXY2K1WYGVRA1VHY8521D1V`
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setApiData(data.result);
        setIsLoading(false);
      });
  }

  return (
    <div className="container">
      <div className="list-of-NGOs">
        <div className="contain">
          <button
            className="first NGO-btn center"
            onClick={fetchTransactionData}
            disabled={isLoading}
          >
            NGO-1
          </button>
        </div>
      </div>
      <div className="outer">
        {!isLoading && apiData.length > 1 && (
          <div className="selected-NGO">
            <h1 className="selected">TRANSACTION HISTORY</h1>
            <ul className="unordered">
              {apiData.map((datum) => (
                <li className="api">
                  FROM:
                  <br /> {datum.from} <br /> TO: <br />
                  {datum.to} <br /> VALUE: <br />{" "}
                  {(parseInt(datum.value) / 10000000000 / 100000000).toString()}{" "}
                  ETH <br /> DATE/TIME :{<DateAndTime time={datum.timeStamp} />}{" "}
                </li>
              ))}
            </ul>
          </div>
        )}
        {!isLoading && apiData.length === 1 && <p>NO DATA.</p>}
        {isLoading && <LoadingSpinner />}
      </div>
    </div>
  );
}
export default TrackTransactions;
