import React, { useRef, useState } from "react";

import "./SignUpAsDonor.css";
function SignUpAsDonor() {
  const [val, setVal] = useState("");
  const emailInputRef = useRef();
  const passwordInputRef = useRef();
  const addressInputRef = useRef();

  const submitHandler = (event) => {
    event.preventDefault();
    const enteredEmail = emailInputRef.current.value;
    const enteredPassword = passwordInputRef.current.value;
    let enteredAddress = addressInputRef.current.value;

    fetch(
      "https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyB0I1gvMRvmO1maRsFbDKMS1K_GPzNt1r0",
      {
        method: "POST",
        body: JSON.stringify({
          email: enteredEmail,
          password: enteredPassword,
          returnSecureToken: true,
        }),
        headers: { "Content-Type": "application/json" },
      }
    );

    fetch(
      "https://login-auth-640ec-default-rtdb.firebaseio.com/DonorWalletAddress.json",
      {
        method: "POST",
        body: JSON.stringify({
          email: enteredEmail,
          address: enteredAddress,
        }),
        headers: { "Content-Type": "application/json" },
      }
    );
  };

  return (
    <section className="sign-up-page">
      <h1>SIGN UP AS DONOR</h1>
      <form onSubmit={submitHandler}>
        <div className="email control">
          <label htmlFor="email">Email</label>
          <input name ="email" type="email" id="email" required ref={emailInputRef} autoComplete="off" value={val}/>
        </div>
        <div className="password control">
          <label htmlFor="password">Password</label>
          <input
          name="password"
            type="password"
            id="password"
            required
            ref={passwordInputRef}
            autoComplete="off"
            value={val}
          />
        </div>
        <div className="walletAddress control">
          <label htmlFor="walletAddress">MetaMask Wallet Address</label>
          <input name= "walletAddress" type="text" id="address" required ref={addressInputRef} autoComplete="off" value={val}/>
        </div>
        <div className="options">
          <button className="create-btn">Create Account</button>
        </div>
      </form>
    </section>
  );
}

export default SignUpAsDonor;
