import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import { paymentABI, paymentAddress } from "../paymentContract/constants";
export default function PaymentGateway() {
  const { ethereum } = window;
  const [amountData, setAmountData] = useState("");
  const [addressData, setAddressData] = useState("");
  const [currentAccount, setCurrentAccount] = useState("");
  const [vendor, setVendor] = useState("");
  const [location, setLocation] = useState("");
  //   const [defaultAccount, setDefaultAccount] = useState("");
  //   const [provider, setProvider] = useState(null);
  const date = new Date();
  //   console.log(date);
  const stringDate = date.toLocaleDateString();

  //   const connectWalletHandler = () => {
  //     if (ethereum && defaultAccount == null) {
  //       // set ethers provider
  //       setProvider(new ethers.providers.Web3Provider(ethereum));

  //       // connect to metamask
  //       ethereum
  //         .request({ method: "eth_requestAccounts" })
  //         .then((result) => {
  //           setDefaultAccount(result[0]);
  //         })
  //         .catch((error) => {
  //           console.log(error.message);
  //         });
  //     } else if (!ethereum) {
  //       console.log("Need to install MetaMask");
  //       console.log("Please install MetaMask browser extension to interact");
  //     }
  //   };

  //   ethereum.on("accountsChanged", function (accounts) {
  //     connectWalletHandler();
  //   });
  //   window.onbeforeunload = function () {
  //     return "Prevent reload";
  //   };

  const createEthereumContract = () => {
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    const paymentContract = new ethers.Contract(
      paymentAddress,
      paymentABI,
      signer
    );
    return paymentContract;
  };

  const paymentContract = createEthereumContract();
  const handleName = (e) => {
    setVendor(e.target.value);
  };
  const handleLocation = (e) => {
    setLocation(e.target.value);
  };

  const handleChange = (e) => {
    setAmountData(e.target.value);
  };
  const handleAddress = (e) => {
    setAddressData(e.target.value);
  };
  ethereum.on("accountsChanged", function () {
    window.location.reload();
  });

  useEffect(() => {
    async function connectWallet() {
      if (typeof ethereum !== "undefined") {
        const accounts = await ethereum.request({
          method: "eth_requestAccounts",
        });
        setCurrentAccount(accounts[0]);
      }
    }
    connectWallet();
  }, [currentAccount, setCurrentAccount, ethereum]);
  const sendETH = async (e) => {
    e.preventDefault();

    try {
      if (ethereum) {
        const parsedAmount = ethers.utils.parseEther(amountData);
        await ethereum.request({
          method: "eth_sendTransaction",
          params: [
            {
              from: currentAccount,
              to: addressData,
              gas: "0x5208",
              value: parsedAmount._hex,
            },
          ],
        });
        const paymentHash = await paymentContract.pay(
          currentAccount,
          addressData,
          vendor,
          location,
          amountData,
          stringDate
        );
        await paymentHash.wait();
        const deets = await paymentContract.getDetails();
        console.log(deets);
      } else {
        console.log("No ethereum object");
      }
    } catch (error) {
      console.log(error);
      throw new Error("No ethereum object");
    }
  };
  return (
    <div className="single-outer">
      <div className="single-card-item-gateway">
        <form className="gateway-form" onSubmit={sendETH}>
          <p className="gateway-lable">TRANSFER TO</p>
          <br />
          <input
            id="transfer-to-account"
            className="transfer-to"
            type="text"
            required
            autoComplete="off"
            onChange={handleAddress}
          />
          <br />
          <p className="gateway-lable">VENDOR NAME</p>
          <br />
          <input
            id="vendor-name"
            className="vendor-name"
            type="text"
            required
            autoComplete="off"
            onChange={handleName}
          />
          <br />
          <p className="gateway-lable">VENDOR LOCATION</p>
          <br />
          <input
            id="vendor-location"
            className="vendor-location"
            type="text"
            required
            autoComplete="off"
            onChange={handleLocation}
          />
          <br />
          <p className="gateway-lable">AMOUNT (ETH)</p>
          <br />
          <input
            id="amount"
            className="amount"
            type="number"
            min={0.000000000000000001}
            step={0.000000000000000001}
            required
            autoComplete="off"
            onChange={handleChange}
          />
          <br />
          <button
            className="variable-width-btn button-su transaction-list-btn"
            type="submit"
          >
            TRANSFER
          </button>
        </form>
      </div>
    </div>
  );
}
