import React from "react";

import "./CampaignCard.css";
const CampaignCardItem = (props) => {
  return (
    <li className="card-item">
      <div className="card-box table">
        <div className="card-preview">
          <h2> {props.ngo}</h2>
          <h3> {props.email}</h3>
          <h3> {props.phone}</h3>
        </div>
        <div className="card-info">
          <table>
            <tr>
              <td>
                <h3>
                  {" "}
                  Objective : <td>{props.objective}</td>
                </h3>
              </td>
            </tr>
            <tr>
              <td>
                <h3>
                  {" "}
                  Minimum Acceptable Donation:<td>{props.minimum}</td>
                </h3>
              </td>
            </tr>
            <tr>
              <td>
                <h3>
                  {" "}
                  Target Amount:<td>{props.target}</td>
                </h3>
              </td>
            </tr>
            <tr>
              <td>
                <h3>
                  {" "}
                  MetaMask Wallet Address:<td>{props.wallet}</td>
                </h3>
              </td>
            </tr>
          </table>
          <button class="btn">Fund Campaign</button>
        </div>
      </div>
    </li>
  );
};

export default CampaignCardItem;
