import { useEffect } from "react";
import useHttp from "./createCampaign/use-http";
import { getSingleCampaign } from "./createCampaign/api";
import TrackTransactions from "./TrackTransactions";
import { useParams } from "react-router-dom";
const Passer = () => {
  //   const { sendRequest, status } = useHttp(getSingleCampaign);
  //   const history = useHistory();
