  //     if (status === "completed") {
  //       history.push("/track");
  //     }
  //   }, [status, history]);
  //   const getTransactionHandler = (campaignData) =>{
  //     sendRequest(campaignData);
  //   }
  const {
    sendRequest,
    status,
    data: loadedCampaigns /*error*/,
  } = useHttp(getSingleCampaign, true);
  const params = useParams();
  const { campaignId } = params;
  useEffect(() => {
    sendRequest(campaignId);
  }, [sendRequest, campaignId]);
  if (
    status === "completed" &&
    (!loadedCampaigns || loadedCampaigns.length === 0)
  ) {
    console.log("NOT FOUND");
  }
  return <TrackTransactions campaigns={loadedCampaigns} />;
};
export default Passer;
