import React, { useState } from "react";
// import Data from "./components/APIData";
import Navbar from "./components/Navbar";
// import Vote from "./components/Vote";
// import CampaignCards from "./components/CampaignCards";
// import Heading from "./components/Heading";
import "./App.css";
import DateAndTime from "./Date.js";
import TrackTransactions from "./components/TrackTransactions";
function App() {
  const [apiData, setApiData] = useState([
    { from: "", to: "", value: "", timeStamp: "" },
  ]);
  function fetchTransactionData() {
    fetch(
      "https://api.etherscan.io/api?module=account&action=txlist&address=0x98388199AeC4c7489b92b09C630fDb022102a429&startblock=0&startblock=0&page=1&offset=20&sort=desc&apikey=NV73WQ1VR3QCXY2K1WYGVRA1VHY8521D1V"
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setApiData(data.result);
      });
  }

  return (
    <div className="App">
      /* <Navbar />
      {/* <Vote /> */}
      {/* <Heading />
       <CampaignCards />
       <CampaignCards />
       <CampaignCards />
       <CampaignCards />
       <CampaignCards />
       <CampaignCards />       

       <CampaignCards /> */}
      <TrackTransactions />
      <button onClick={fetchTransactionData}>GET</button>
      {apiData.length > 1 && (
        <ul className="unordered">
          {apiData.map((datum) => (
            <li className="api">
              FROM: {datum.from} <br /> TO: {datum.to} <br /> VALUE:{" "}
              {(parseInt(datum.value) / 10000000000 / 100000000).toString()} ETH{" "}
              <br /> DATE/TIME :{<DateAndTime time={datum.timeStamp} />}{" "}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
export default App;
