import React, { useEffect, useRef, useState } from "react";
import { ethers } from "ethers";
import { contractABI, contractAddress } from "../utils/constants";
import "./CampaignCard.css";
import TrackTransactions from "../TrackTransactions";
import LoadingSpinner from "../LoadingSpinner";
const CampaignCardItem = (props) => {
  const { ethereum } = window;
  let sub1, sub2;
  const [isLoading, setIsLoading] = useState(false);
  const [buttonText, setButtonText] = useState("Transfer");
  // const [val, setVal] = useState(props.minimum);
  const [currentAccount, setCurrentAccount] = useState("");
  const [formData, setformData] = useState("");
  const [isFund, setIsFund] = useState(false);
  const [isAllowed, setIsAllowed] = useState(false);
  const [campaignBalance, setCampaignBalance] = useState(0);
  const [noTransfer, setNoTransfer] = useState(false);
  const [transferDone, setTransferDone] = useState(false);
  const [preview, setPreview] = useState(false);
  const btnRef = useRef();
  const addressTo = props.wallet;
  // const min = props.minimum;
  // const max = props.target;
  const changeText = (text) => setButtonText(text);
  const createEthereumContract = () => {
    const provider = new ethers.providers.Web3Provider(ethereum);
    const signer = provider.getSigner();
    const transactionsContract = new ethers.Contract(
      contractAddress,
      contractABI,
      signer
    );
    return transactionsContract;
  };

  const transactionsContract = createEthereumContract();

  useEffect(() => {
    fetch(
      `https://api-goerli.etherscan.io/api?module=account&action=balance&address=${props.wallet}&tag=latest&apikey=NV73WQ1VR3QCXY2K1WYGVRA1VHY8521D1V`
    )
      .then((response) => {
        return response.json();
      })
      .then((data) => {
        setCampaignBalance(data.result);
      });
  }, []);

  const handleChange = (e) => {
    setformData(e.target.value);
    // console.log(min);
    // console.log(max);
    // const value = Math.max(min, Math.min(max, Number(e.target.value)));
    // setVal(value);
  };
  ethereum.on("accountsChanged", function () {
    window.location.reload();
  });
  useEffect(() => {
    async function connectWallet() {
      if (typeof ethereum !== "undefined") {
        const accounts = await ethereum.request({
          method: "eth_requestAccounts",
        });
        setCurrentAccount(accounts[0]);
      }
    }
    connectWallet();
  }, [currentAccount, setCurrentAccount, ethereum]);

  const sendETH = async (e) => {
    e.preventDefault();

    try {
      if (ethereum) {
        const parsedAmount = ethers.utils.parseEther(formData);
        await ethereum.request({
          method: "eth_sendTransaction",
          params: [
            {
              from: currentAccount,
              to: addressTo,
              gas: "0x5208",
              value: parsedAmount._hex,
            },
          ],
        });

        const transactionHash = await transactionsContract.fund(addressTo);
        setIsLoading(true);
        await transactionHash.wait();
        setIsLoading(false);
        changeText("Transferred");
        setTransferDone(true);
      } else {
        console.log("No ethereum object");
        setIsLoading(false);
      }
    } catch (error) {
      setIsLoading(false);
      console.log(error);
      throw new Error("No ethereum object");
    }
  };
  function fundCampaignHandler() {
    setIsFund(true);

    if (
      (parseInt(campaignBalance) / 10000000000 / 100000000).toFixed(6) ===
      props.target.toString()
    ) {
      setNoTransfer(true);
    }
  }

  async function handleList() {
    const enter = await transactionsContract.checkIfFunder(
      currentAccount,
      addressTo
    );
    console.log(enter);
    setIsAllowed(enter);
    if (!enter) {
      alert("Not a Donor");
    }
  }

  return (
    <div className="campaign-page-main">
      <div className="card-preview" onClick={() => setPreview(true)}>
        <h2> {props.ngo}</h2>
        <h4> {props.email}</h4>
        <h4> {props.phone}</h4>
      </div>
      {preview && (
        <div className="outer">
          <div className="card-item">
            <div className="card-box table">
              <div className="card-info">
                <h3>
                  <table className="table-content">
                    <tbody>
                      <tr>
                        <td>
                          {" "}
                          Mission : <u>{props.objective} </u>
                        </td>
                      </tr>
                      <tr>
                        <td>
                          {" "}
                          Minimum Acceptable Donation :{" "}
                          <u>{props.minimum} ETH</u>
                        </td>
                      </tr>
                      <tr>
                        <td>
                          {" "}
                          Target Amount : <u>{props.target} ETH</u>
                        </td>
                      </tr>
                      <tr>
                        <td>
                          {" "}
                          MetaMask Wallet Address : <u>{props.wallet}</u>
                        </td>
                      </tr>

                      <tr>
                        <td>
                          <p>
                            Current Balance:{" "}
                            <u>
                              {(
                                parseInt(campaignBalance) /
                                10000000000 /
                                100000000
                              ).toFixed(6)}{" "}
                              ETH
                            </u>
                          </p>
                        </td>
                      </tr>
                    </tbody>
                  </table>
                </h3>

                {!isFund && (
                  <div className="container1">
                    <div className="center1">
                      <button
                        className="fund-campaign-btn"
                        onClick={fundCampaignHandler}
                        ref={btnRef}
                      >
                        Fund Campaign
                      </button>
                    </div>
                  </div>
                )}
                {noTransfer && (
                  <>
                    <p className="done">TARGET REACHED. THANK YOU.</p>
                    <div className="container2">
                      <div className="center2">
                        <button
                          className="fund-campaign-btn1"
                          onClick={handleList}
                        >
                          Transaction List
                        </button>
                      </div>
                    </div>
                  </>
                )}

                {isFund && !noTransfer && (
                  <form onSubmit={sendETH}>
                    {!transferDone && (
                      <input
                        className="fund-campaign-input"
                        name="amount"
                        type="number"
                        placeholder="ETH"
                        min={props.minimum}
                        step={0.0001}
                        onChange={handleChange}
                      />
                    )}

                    {!isLoading && (
                      <button type="submit" className="fund-campaign-btn1">
                        {buttonText}
                      </button>
                    )}
                    {isLoading && <LoadingSpinner />}
                  </form>
                )}
                {!isFund && (
                  <div className="container2">
                    <div className="center2">
                      <button
                        className="fund-campaign-btn1"
                        onClick={handleList}
                      >
                        Transaction List
                      </button>
                    </div>
                  </div>
                )}
                {transferDone && (
                  <div className="container2">
                    <div className="center2">
                      <button
                        className="fund-campaign-btn1"
                        onClick={handleList}
                      >
                        Transaction List
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
          {isAllowed && (
            <div className="track">
              <TrackTransactions address={props.wallet} />
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default CampaignCardItem;
