import { useEffect, useState } from "react";
import { detailsABI, detailsAddress } from "../detailsContract/constants";
import { ethers } from "ethers";
import Temporary from "./Temporary";
// let allCampaigns={};
let all;
const GetCampaign = () => {
  const [isLoading, setIsLoading] = useState(true);
  const [loadedCampaigns, setLoadedCampaigns] = useState([{}]);
  const { ethereum } = window;
  
  const start = async()=>{
    const createEthereumContract = () => {
      const provider = new ethers.providers.Web3Provider(ethereum);
      const signer = provider.getSigner();
      const detailsContract = new ethers.Contract(
        detailsAddress,
        detailsABI,
        signer
      );
      return detailsContract;
    };
    const detailsContract = createEthereumContract();
    const allCampaigns = await detailsContract.getCampaigns();
    console.log(allCampaigns);
    setLoadedCampaigns(allCampaigns);
    setIsLoading(false);
  }
  start();
  return <>{!isLoading &&<Temporary campaigns={loadedCampaigns} />}</>
};
export default GetCampaign;
